func:
// Rate limit unknown protocols
const lastWarn = context.get("lastProtocolWarn") || 0;
const now = Date.now();
if (now - lastWarn < 60000) return null; // Only warn once per minute
context.set("lastProtocolWarn", now);

// Universal data normalization for all protocols
// Converts protocol-specific data to common format

const protocolData = msg.payload;
const protocol = msg.protocol || 'unknown';
const timestamp = new Date();

// Common data structure for all protocols
const normalizedData = {
    // Metadata
    id: generateDataId(),
    timestamp: timestamp.toISOString(),
    timestampMs: timestamp.getTime(),

    // Source information
    source: {
        protocol: protocol,
        deviceId: protocolData.deviceId || 'unknown',
        address: protocolData.address || '',
        channel: protocolData.channel || 0,
        raw: protocolData.raw || null
    },

    // Equipment mapping
    equipment: {
        id: protocolData.equipmentId || null,
        name: protocolData.equipmentName || null,
        type: protocolData.equipmentType || null,
        location: protocolData.location || null
    },

    // Normalized value
    data: {
        value: null,
        unit: protocolData.unit || '',
        dataType: 'unknown',
        quality: 'Good',
        scaled: false
    },

    // Status
    status: {
        online: true,
        error: null,
        lastError: null
    }
};

// Protocol-specific normalization
switch(protocol) {
    case 'mqtt':
        normalizedData.data.value = protocolData.value;
        normalizedData.data.dataType = typeof protocolData.value;
        normalizedData.source.address = protocolData.topic;
        break;

    case 'phidget':
        normalizedData.data.value = protocolData.value;
        normalizedData.data.dataType = protocolData.dataType || 'float';
        normalizedData.source.deviceId = protocolData.serialNumber;
        normalizedData.source.channel = protocolData.channel;
        break;

    case 'modbus':
        normalizedData.data.value = protocolData.value;
        normalizedData.data.dataType = protocolData.registerType || 'int16';
        normalizedData.source.address = `${protocolData.unitId}:${protocolData.register}`;
        normalizedData.data.scaled = protocolData.scaled || false;
        break;

    case 'ethernetip':
        normalizedData.data.value = protocolData.value;
        normalizedData.data.dataType = protocolData.dataType || 'REAL';
        normalizedData.source.address = protocolData.tagName;
        break;

    case 'opcua':
        normalizedData.data.value = protocolData.value.value;
        normalizedData.data.dataType = protocolData.dataType;
        normalizedData.source.address = protocolData.nodeId;
        normalizedData.data.quality = protocolData.statusCode?.name || 'Good';
        break;

    default:
        // Rate limited: node.warn(`Unknown protocol: ${protocol}`);
}

// Apply equipment mapping if available
const equipmentMap = flow.get('equipment_protocol_map') || {};
const mapKey = `${protocol}:${normalizedData.source.deviceId}:${normalizedData.source.channel}`;

if (equipmentMap[mapKey]) {
    const mapping = equipmentMap[mapKey];
    normalizedData.equipment = {
        id: mapping.equipmentId,
        name: mapping.equipmentName,
        type: mapping.equipmentType,
        location: mapping.location
    };
    normalizedData.data.unit = mapping.unit || normalizedData.data.unit;
}

// Store in time-series buffer
storeInBuffer(normalizedData);

// Set message properties
msg.payload = normalizedData;
msg.topic = `data/${protocol}/${normalizedData.equipment.id || normalizedData.source.deviceId}`;

return msg;

// Helper functions
function generateDataId() {
    return `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
}

function storeInBuffer(data) {
    let buffer = flow.get('protocol_data_buffer') || [];
    buffer.push(data);

    // Keep last 10000 entries
    if (buffer.length > 10000) {
        buffer = buffer.slice(-10000);
    }

    flow.set('protocol_data_buffer', buffer);
}

// Route normalized data based on equipment registration
const data = msg.payload;

// Get routing rules
const routingRules = flow.get('data_routing_rules') || {};
const routes = [];

// Check equipment-based routing
if (data.equipment.id) {
    const equipmentRoutes = routingRules[data.equipment.id] || [];
    routes.push(...equipmentRoutes);
}

// Check protocol-based routing
const protocolRoutes = routingRules[`protocol:${data.source.protocol}`] || [];
routes.push(...protocolRoutes);

// Default routes
if (routes.length === 0) {
    routes.push({
        type: 'mqtt',
        topic: `brewery/data/${data.source.protocol}/${data.equipment.name || data.source.deviceId}`,
        retain: false
    });
}

// Create messages for each route
const messages = routes.map(route => {
    const routeMsg = {
        payload: data,
        topic: route.topic
    };

    // Add route-specific properties
    switch(route.type) {
        case 'mqtt':
            routeMsg.retain = route.retain || false;
            routeMsg.qos = route.qos || 0;
            break;
        case 'database':
            routeMsg.collection = route.collection;
            routeMsg.measurement = route.measurement;
            break;
        case 'opcua':
            routeMsg.nodeId = route.nodeId;
            break;
    }

    routeMsg._route = route;
    return routeMsg;
});

// Send to appropriate outputs
// Output 1: MQTT routes
// Output 2: Database routes  
// Output 3: OPC UA routes
// Output 4: Other routes

const mqttMessages = messages.filter(m => m._route.type === 'mqtt');
const dbMessages = messages.filter(m => m._route.type === 'database');
const opcMessages = messages.filter(m => m._route.type === 'opcua');
const otherMessages = messages.filter(m => !['mqtt', 'database', 'opcua'].includes(m._route.type));

return [mqttMessages, dbMessages, opcMessages, otherMessages];

// Monitor data quality and device health
const data = msg.payload;
const deviceKey = `${data.source.protocol}:${data.source.deviceId}`;

// Get or initialize device health tracking
let deviceHealth = flow.get('device_health') || {};

if (!deviceHealth[deviceKey]) {
    deviceHealth[deviceKey] = {
        deviceId: data.source.deviceId,
        protocol: data.source.protocol,
        firstSeen: data.timestamp,
        lastSeen: data.timestamp,
        messageCount: 0,
        errorCount: 0,
        status: 'online',
        quality: {
            good: 0,
            uncertain: 0,
            bad: 0
        },
        statistics: {
            min: null,
            max: null,
            avg: null,
            lastValue: null
        }
    };
}

// Update device health
const health = deviceHealth[deviceKey];
health.lastSeen = data.timestamp;
health.messageCount++;
health.lastValue = data.data.value;

// Track quality
if (data.data.quality === 'Good') {
    health.quality.good++;
} else if (data.data.quality === 'Uncertain') {
    health.quality.uncertain++;
} else {
    health.quality.bad++;
}

// Update statistics for numeric values
if (typeof data.data.value === 'number') {
    if (health.statistics.min === null || data.data.value < health.statistics.min) {
        health.statistics.min = data.data.value;
    }
    if (health.statistics.max === null || data.data.value > health.statistics.max) {
        health.statistics.max = data.data.value;
    }

    // Calculate running average
    if (health.statistics.avg === null) {
        health.statistics.avg = data.data.value;
    } else {
        health.statistics.avg = (health.statistics.avg * (health.messageCount - 1) + data.data.value) / health.messageCount;
    }
}

// Check for anomalies
const anomalies = [];

// Stale data check
const timeSinceLastMessage = Date.now() - new Date(health.lastSeen).getTime();
if (timeSinceLastMessage > 60000) { // 1 minute
    anomalies.push({
        type: 'stale_data',
        message: `No data received for ${Math.round(timeSinceLastMessage / 1000)}s`,
        severity: 'warning'
    });
}

// Quality degradation check
const totalQuality = health.quality.good + health.quality.uncertain + health.quality.bad;
const goodPercentage = (health.quality.good / totalQuality) * 100;
if (goodPercentage < 90 && totalQuality > 100) {
    anomalies.push({
        type: 'quality_degradation',
        message: `Data quality at ${goodPercentage.toFixed(1)}%`,
        severity: 'warning'
    });
}

// Value range check (if configured)
const rangeConfig = flow.get('value_range_config') || {};
const range = rangeConfig[data.equipment.id];
if (range && typeof data.data.value === 'number') {
    if (data.data.value < range.min || data.data.value > range.max) {
        anomalies.push({
            type: 'out_of_range',
            message: `Value ${data.data.value} outside range [${range.min}, ${range.max}]`,
            severity: 'critical'
        });
    }
}

// Update status
if (anomalies.length > 0) {
    health.status = 'warning';
    health.anomalies = anomalies;
} else {
    health.status = 'online';
    health.anomalies = [];
}

// Save updated health
deviceHealth[deviceKey] = health;
flow.set('device_health', deviceHealth);

// Send alerts if needed
if (anomalies.length > 0) {
    msg.payload = {
        deviceKey: deviceKey,
        device: health,
        anomalies: anomalies,
        timestamp: new Date().toISOString()
    };
    msg.topic = 'device/alert';
    return msg;
}

return null;

// Manage protocol modules and their status
const action = msg.payload.action;
const protocol = msg.payload.protocol;

// Get protocol registry
let protocols = flow.get('protocol_registry') || {
    mqtt: {
        name: 'MQTT',
        enabled: true,
        status: 'active',
        config: {},
        stats: { messages: 0, errors: 0 }
    },
    phidget: {
        name: 'Phidget',
        enabled: true,
        status: 'active',
        config: {},
        stats: { messages: 0, errors: 0 }
    },
    modbus: {
        name: 'Modbus TCP/RTU',
        enabled: false,
        status: 'disabled',
        config: {},
        stats: { messages: 0, errors: 0 }
    },
    ethernetip: {
        name: 'Ethernet/IP',
        enabled: false,
        status: 'disabled',
        config: {},
        stats: { messages: 0, errors: 0 }
    },
    opcua: {
        name: 'OPC UA',
        enabled: false,
        status: 'disabled',
        config: {},
        stats: { messages: 0, errors: 0 }
    }
};

switch(action) {
    case 'enable':
        if (protocols[protocol]) {
            protocols[protocol].enabled = true;
            protocols[protocol].status = 'starting';
            msg.payload = {
                action: 'start',
                protocol: protocol,
                config: protocols[protocol].config
            };
            msg.topic = `protocol/${protocol}/control`;
        }
        break;

    case 'disable':
        if (protocols[protocol]) {
            protocols[protocol].enabled = false;
            protocols[protocol].status = 'stopping';
            msg.payload = {
                action: 'stop',
                protocol: protocol
            };
            msg.topic = `protocol/${protocol}/control`;
        }
        break;

    case 'configure':
        if (protocols[protocol]) {
            protocols[protocol].config = msg.payload.config;
            msg.payload = {
                action: 'reconfigure',
                protocol: protocol,
                config: msg.payload.config
            };
            msg.topic = `protocol/${protocol}/control`;
        }
        break;

    case 'status_update':
        if (protocols[protocol]) {
            protocols[protocol].status = msg.payload.status;
            if (msg.payload.stats) {
                protocols[protocol].stats = msg.payload.stats;
            }
        }
        break;

    case 'register_new':
        // Register a new protocol module
        protocols[protocol] = {
            name: msg.payload.name,
            enabled: false,
            status: 'registered',
            config: msg.payload.config || {},
            stats: { messages: 0, errors: 0 }
        };
        break;
}

// Save updated registry
flow.set('protocol_registry', protocols);

// Return control message if needed
if (['enable', 'disable', 'configure'].includes(action)) {
    return msg;
}

return null;

// Handle device alerts and anomalies
const alert = msg.payload;

// Get alert configuration
const alertConfig = flow.get('alert_config') || {
    email: false,
    mqtt: true,
    dashboard: true,
    log: true
};

// Create alert message
const alertMessage = {
    timestamp: alert.timestamp,
    device: alert.deviceKey,
    protocol: alert.device.protocol,
    status: alert.device.status,
    anomalies: alert.anomalies,
    severity: Math.max(...alert.anomalies.map(a => 
        a.severity === 'critical' ? 3 : a.severity === 'warning' ? 2 : 1
    ))
};

// Store in alert history
let alertHistory = flow.get('alert_history') || [];
alertHistory.unshift(alertMessage);
if (alertHistory.length > 500) {
    alertHistory = alertHistory.slice(0, 500);
}
flow.set('alert_history', alertHistory);

// Send to configured destinations
const messages = [];

if (alertConfig.mqtt) {
    messages.push({
        payload: alertMessage,
        topic: `brewery/alerts/${alert.device.protocol}/${alert.deviceKey}`
    });
}

if (alertConfig.dashboard) {
    messages.push({
        payload: alertMessage,
        topic: 'dashboard/alert'
    });
}

return messages;

// Map equipment to protocol addresses
const action = msg.payload.action;

if (action === 'map') {
    const mapping = msg.payload.mapping;
    const mapKey = `${mapping.protocol}:${mapping.deviceId}:${mapping.channel || 0}`;

    // Get current mappings
    let equipmentMap = flow.get('equipment_protocol_map') || {};

    // Add new mapping
    equipmentMap[mapKey] = {
        equipmentId: mapping.equipmentId,
        equipmentName: mapping.equipmentName,
        equipmentType: mapping.equipmentType,
        location: mapping.location,
        unit: mapping.unit,
        scaling: mapping.scaling || null
    };

    // Save mappings
    flow.set('equipment_protocol_map', equipmentMap);

    msg.payload = {
        success: true,
        message: 'Equipment mapped successfully',
        mapKey: mapKey
    };

} else if (action === 'unmap') {
    const mapKey = msg.payload.mapKey;
    let equipmentMap = flow.get('equipment_protocol_map') || {};

    delete equipmentMap[mapKey];
    flow.set('equipment_protocol_map', equipmentMap);

    msg.payload = {
        success: true,
        message: 'Equipment unmapped'
    };

} else if (action === 'list') {
    const equipmentMap = flow.get('equipment_protocol_map') || {};
    msg.payload = equipmentMap;
}

return msg;

// Store individual form field values in flow context
let formData = flow.get('equipmentFormData') || {
    equipmentType: '',
    equipmentName: '',
    location: '',
    serialNumber: '',
    enableOPC: false
};

// Update the specific field that was changed
switch(msg.topic) {
    case 'equipmentType':
        formData.equipmentType = msg.payload;
        break;
    case 'equipmentName':
        formData.equipmentName = msg.payload;
        break;
    case 'location':
        formData.location = msg.payload;
        break;
    case 'serialNumber':
        formData.serialNumber = msg.payload;
        break;
    case 'enableOPC':
        formData.enableOPC = msg.payload;
        break;
}

// Save updated form data
flow.set('equipmentFormData', formData);

// Log the update
node.status({fill:"green",shape:"dot",text:msg.topic + ": " + msg.payload});

return msg;

// Get stored form data
const formData = flow.get('equipmentFormData') || {};

// Debug log
node.warn("Registration attempt with data:");
node.warn(JSON.stringify(formData, null, 2));

// Validate required fields
const errors = [];

if (!formData.equipmentType || formData.equipmentType === '') {
    errors.push("Please select an equipment type");
}

if (!formData.equipmentName || formData.equipmentName.trim() === '') {
    errors.push("Please enter an equipment name");
}

if (!formData.location || formData.location === '') {
    errors.push("Please select a location");
}

// If errors, send to error output
if (errors.length > 0) {
    msg.payload = {
        success: false,
        error: errors.join('. ')
    };
    node.error("Validation failed: " + errors.join(', '));
    return [null, msg];
}

// Sanitize equipment name
const sanitizedName = formData.equipmentName.trim().replace(/[^a-zA-Z0-9_-]/g, '_');

// Build registration data
const registrationData = {
    action: "register",
    equipmentType: formData.equipmentType,
    equipmentName: sanitizedName,
    originalName: formData.equipmentName.trim(),
    location: formData.location,
    serialNumber: formData.serialNumber || "",
    enableOPC: formData.enableOPC || false,
    timestamp: new Date().toISOString(),
    registeredAt: new Date().toLocaleString()
};

msg.payload = registrationData;
msg.topic = "equipment/registration";

// Clear form data after successful validation
flow.set('equipmentFormData', {
    equipmentType: '',
    equipmentName: '',
    location: '',
    serialNumber: '',
    enableOPC: false
});

// Log success
node.warn("Equipment registration validated successfully");
node.warn("OPC enabled: " + registrationData.enableOPC);

return [msg, null];

// Notify that OPC was skipped
msg.notification = {
    payload: "Equipment registered successfully (OPC disabled)",
    topic: "success"
};

// Pass through the original message
return msg;

// Only build OPC tags if enabled
if (!msg.payload.enableOPC) {
    return null;
}

// Build OPC UA tag structure for equipment
const data = msg.payload;
const basePath = `Registration/Data/${data.location}/${data.equipmentName}`;

// Create folder structure
const folders = [
    `Registration`,
    `Registration/Data`,
    `Registration/Data/${data.location}`,
    basePath
];

// Create registration tags based on equipment type
const tags = [
    {
        path: `${basePath}/type`,
        value: data.equipmentType,
        dataType: "String"
    },
    {
        path: `${basePath}/name`,
        value: data.equipmentName,
        dataType: "String"
    },
    {
        path: `${basePath}/location`,
        value: data.location,
        dataType: "String"
    },
    {
        path: `${basePath}/serialNumber`,
        value: data.serialNumber,
        dataType: "String"
    },
    {
        path: `${basePath}/registeredAt`,
        value: data.timestamp,
        dataType: "String"
    },
    {
        path: `${basePath}/status`,
        value: "registered",
        dataType: "String"
    }
];

// Add equipment-specific tags
switch(data.equipmentType) {
    case "pump":
        tags.push(
            { path: `${basePath}/flow_rate`, value: 0, dataType: "Float" },
            { path: `${basePath}/pressure`, value: 0, dataType: "Float" },
            { path: `${basePath}/running`, value: false, dataType: "Boolean" }
        );
        break;
    case "temperature_sensor":
        tags.push(
            { path: `${basePath}/temperature`, value: 0, dataType: "Float" },
            { path: `${basePath}/unit`, value: "F", dataType: "String" }
        );
        break;
    case "pressure_sensor":
        tags.push(
            { path: `${basePath}/pressure`, value: 0, dataType: "Float" },
            { path: `${basePath}/unit`, value: "PSI", dataType: "String" }
        );
        break;
    case "valve":
        tags.push(
            { path: `${basePath}/position`, value: 0, dataType: "Float" },
            { path: `${basePath}/state`, value: "closed", dataType: "String" }
        );
        break;
}

msg.folders = folders;
msg.tags = tags;
msg.equipmentData = data;

return msg;

// Check if OPC connection is available
// For now, we'll simulate this check
// In production, this would test the actual OPC connection

// Get OPC status from flow context
let opcAvailable = flow.get("opcAvailable");

// If not set, default to false for safety
if (opcAvailable === undefined) {
    opcAvailable = false;
    flow.set("opcAvailable", false);
}

if (!opcAvailable) {
    // OPC not available, skip OPC operations
    msg.notification = {
        payload: "Equipment registered (OPC server not available)",
        topic: "warning"
    };
    return [null, msg];
}

// OPC is available, proceed
return [msg, null];

// Placeholder for OPC folder creation
// In production, this would interface with the OPC UA client

msg.notification = {
    payload: "Equipment registered with OPC tags",
    topic: "success"
};

return msg;

// Get current equipment list from flow context
let equipment = flow.get("registeredEquipment") || [];

// Create equipment entry
const newEquipment = {
    id: Date.now().toString(),
    type: msg.payload.equipmentType,
    name: msg.payload.originalName,
    location: msg.payload.location,
    serialNumber: msg.payload.serialNumber,
    registeredAt: msg.payload.registeredAt,
    opcEnabled: msg.payload.enableOPC,
    status: "active"
};

// Add to equipment list
equipment.push(newEquipment);

// Store updated list
flow.set("registeredEquipment", equipment);

// Send equipment list to display
msg.payload = equipment;

return msg;

// Send messages to clear the form inputs
// This will reset the UI elements to empty state

const messages = [
    { payload: "", topic: "equipmentType", _topic: "equipmentType" },
    { payload: "", topic: "equipmentName", _topic: "equipmentName" },
    { payload: "", topic: "location", _topic: "location" },
    { payload: "", topic: "serialNumber", _topic: "serialNumber" },
    { payload: false, topic: "enableOPC", _topic: "enableOPC" }
];

return [messages];

// Format equipment list for HTML display
const equipment = msg.payload;

if (!equipment || equipment.length === 0) {
    msg.payload = "<p style='text-align:center; color:#666;'>No equipment registered yet</p>";
    return msg;
}

// Build HTML table
let html = `
<style>
    .equipment-table {
        width: 100%;
        border-collapse: collapse;
        font-size: 14px;
    }
    .equipment-table th {
        background-color: #2196F3;
        color: white;
        padding: 10px;
        text-align: left;
        border-bottom: 2px solid #ddd;
    }
    .equipment-table td {
        padding: 8px;
        border-bottom: 1px solid #ddd;
    }
    .equipment-table tr:nth-child(even) {
        background-color: #f2f2f2;
    }
    .equipment-table tr:hover {
        background-color: #e8e8e8;
    }
    .opc-enabled {
        color: #4CAF50;
        font-weight: bold;
    }
    .opc-disabled {
        color: #9E9E9E;
    }
</style>
<table class='equipment-table'>
    <thead>
        <tr>
            <th>Type</th>
            <th>Name</th>
            <th>Location</th>
            <th>Serial #</th>
            <th>OPC</th>
            <th>Registered</th>
        </tr>
    </thead>
    <tbody>
`;

// Add each equipment row
equipment.forEach(eq => {
    const opcStatus = eq.opcEnabled ? 
        "<span class='opc-enabled'>âœ“</span>" : 
        "<span class='opc-disabled'>âœ—</span>";

    html += `
        <tr>
            <td>${eq.type.replace(/_/g, ' ')}</td>
            <td><strong>${eq.name}</strong></td>
            <td>${eq.location.replace(/_/g, ' ')}</td>
            <td>${eq.serialNumber || '-'}</td>
            <td style='text-align:center;'>${opcStatus}</td>
            <td>${eq.registeredAt}</td>
        </tr>
    `;
});

html += `
    </tbody>
</table>
`;

msg.payload = html;
return msg;

// Initialize equipment registry if not exists
let equipment = flow.get("registeredEquipment");

if (!equipment) {
    equipment = [];
    flow.set("registeredEquipment", equipment);
    node.warn("Initialized empty equipment registry");
}

// Initialize form data
flow.set('equipmentFormData', {
    equipmentType: '',
    equipmentName: '',
    location: '',
    serialNumber: '',
    enableOPC: false
});

// Set OPC availability (default to false for safety)
flow.set("opcAvailable", false);

msg.payload = equipment;
return msg;

// Store OPC availability status
flow.set("opcAvailable", msg.payload);

if (msg.payload) {
    node.status({fill:"green",shape:"dot",text:"OPC Available"});
} else {
    node.status({fill:"red",shape:"ring",text:"OPC Unavailable"});
}

return msg;

// Get current equipment list
let equipment = flow.get("registeredEquipment") || [];

msg.payload = equipment;
return msg;

// Route to appropriate registration form based on equipment type
const equipmentType = msg.payload.equipmentType;

// Initialize registration object
const registration = {
    id: Date.now().toString(),
    equipmentType: equipmentType,
    createdAt: new Date().toISOString(),
    status: 'draft'
};

// Store in context
flow.set('current_registration', registration);

// Route based on type
msg.topic = `form/${equipmentType}`;
msg.registration = registration;

return msg;

// Process equipment registration
const action = msg.payload.action;
const data = msg.payload.data;

// Get or create registrations storage
let registrations = flow.get('equipment_registrations') || {};

if (action === 'register') {
    // Generate unique ID
    const equipmentId = `${data.area}_${data.workCenter}_${data.name}`.toUpperCase();

    // Build ISA-95 path
    const isa95Path = [
        data.enterprise,
        data.site,
        data.area,
        data.workCenter,
        data.workUnit || data.name
    ].filter(Boolean).join('/');

    // Create registration object
    const registration = {
        id: equipmentId,
        ...data,
        isa95Path: isa95Path,
        tagPath: `[default]Enterprise/${data.enterprise}/${data.site}/${data.area}/${data.workCenter}/${data.name}`,
        registeredAt: new Date().toISOString(),
        status: 'active',
        version: 1
    };

    // Check for duplicates
    if (registrations[equipmentId]) {
        msg.payload = {
            success: false,
            error: 'Equipment with this ID already exists',
            existingId: equipmentId
        };
        return [null, msg];
    }

    // Save registration
    registrations[equipmentId] = registration;
    flow.set('equipment_registrations', registrations);

    // Generate Ignition configuration
    msg.payload = {
        success: true,
        registration: registration,
        ignitionConfig: generateIgnitionConfig(registration)
    };

    return [msg, null];

} else if (action === 'save_draft') {
    // Save as draft
    const draftId = `DRAFT_${Date.now()}`;
    const drafts = flow.get('equipment_drafts') || {};

    drafts[draftId] = {
        ...data,
        savedAt: new Date().toISOString(),
        status: 'draft'
    };

    flow.set('equipment_drafts', drafts);

    msg.payload = {
        success: true,
        message: 'Draft saved successfully',
        draftId: draftId
    };

    return [null, msg];
}

// Helper function to generate Ignition configuration
function generateIgnitionConfig(reg) {
    const config = {
        tagProvider: 'default',
        tagPath: reg.tagPath,
        tagType: 'AtomicTag',
        valueSource: 'opc',
        dataType: reg.dataType || 'Float',
        engUnit: reg.units,
        engLow: reg.rangeMin,
        engHigh: reg.rangeMax,
        scaleLow: reg.scaleMin || 4,
        scaleHigh: reg.scaleMax || 20,
        documentation: reg.description,
        alarms: []
    };

    // Add alarms if configured
    if (reg.alarmHH !== undefined) {
        config.alarms.push({
            name: 'HiHi',
            setpoint: reg.alarmHH,
            priority: 'Critical',
            deadband: reg.deadband || 0
        });
    }

    if (reg.alarmH !== undefined) {
        config.alarms.push({
            name: 'Hi',
            setpoint: reg.alarmH,
            priority: 'High',
            deadband: reg.deadband || 0
        });
    }

    if (reg.alarmL !== undefined) {
        config.alarms.push({
            name: 'Lo',
            setpoint: reg.alarmL,
            priority: 'High',
            deadband: reg.deadband || 0
        });
    }

    if (reg.alarmLL !== undefined) {
        config.alarms.push({
            name: 'LoLo',
            setpoint: reg.alarmLL,
            priority: 'Critical',
            deadband: reg.deadband || 0
        });
    }

    // Add connection-specific configuration
    switch(reg.connectionType) {
        case 'mqtt':
            config.opcItemPath = `ns=1;s=[MQTT]${reg.mqttTopic}`;
            break;
        case 'modbus':
            config.opcItemPath = `ns=1;s=[${reg.modbusIP}]${reg.modbusUnit}.HR${reg.modbusRegister}`;
            break;
        case 'phidget':
            config.opcItemPath = `ns=1;s=[Phidget]${reg.phidgetSerial}.${reg.phidgetChannel}`;
            break;
    }

    return config;
}

return null;

