// Safety check added by fix script
const config = msg.config || {};
if (!config.simulateEvents) return null;

// Simulate various types of events for testing
const debugConfig = flow.get('debugConfig') || {};

if (!debugConfig.(eventSettings && eventSettings.simulateEvents)) {
    return null;
}

// Simulated equipment
const equipment = [
    { id: 'simulated_tank_1', type: 'level_sensor', unit: '%' },
    { id: 'simulated_pump_1', type: 'flow_meter', unit: 'GPM' },
    { id: 'simulated_temp_1', type: 'temperature_sensor', unit: 'Â°F' },
    { id: 'simulated_pressure_1', type: 'pressure_sensor', unit: 'PSI' }
];

// Get simulation state
let simState = flow.get('simulation_state') || {
    tank_level: 50,
    pump_flow: 75,
    temperature: 68,
    pressure: 14.5,
    pump_running: true,
    oscillation_count: 0
};

// Generate different event patterns based on rate
const rate = debugConfig.eventSettings.eventGenerationRate;
const events = [];

// Pattern 1: Normal operation with small variations
if (Math.random() < 0.7) {
    // Tank level change
    simState.tank_level += (Math.random() - 0.5) * 2;
    simState.tank_level = Math.max(0, Math.min(100, simState.tank_level));

    events.push({
        payload: {
            equipment: { id: 'simulated_tank_1', type: 'level_sensor' },
            data: {
                value: simState.tank_level,
                unit: '%',
                dataType: 'number'
            },
            timestamp: new Date().toISOString()
        }
    });
}

// Pattern 2: Pump state changes (less frequent)
if (Math.random() < 0.1) {
    simState.pump_running = !simState.pump_running;
    events.push({
        payload: {
            equipment: { id: 'simulated_pump_1', type: 'pump' },
            data: {
                value: simState.pump_running ? 'running' : 'stopped',
                dataType: 'string'
            },
            timestamp: new Date().toISOString()
        }
    });

    // Flow follows pump state
    simState.pump_flow = simState.pump_running ? 75 + Math.random() * 10 : 0;
}

// Pattern 3: Alarm conditions (rare)
if (rate === 'high' && Math.random() < 0.05) {
    // High tank level
    simState.tank_level = 90 + Math.random() * 8;
    events.push({
        payload: {
            equipment: { id: 'simulated_tank_1', type: 'level_sensor' },
            data: {
                value: simState.tank_level,
                unit: '%',
                dataType: 'number'
            },
            timestamp: new Date().toISOString()
        }
    });
}

// Pattern 4: Oscillation (for correlation testing)
if (rate === 'high' && Math.random() < 0.2) {
    simState.oscillation_count++;
    if (simState.oscillation_count < 10) {
        // Create oscillating temperature
        simState.temperature = 68 + Math.sin(simState.oscillation_count) * 5;
        events.push({
            payload: {
                equipment: { id: 'simulated_temp_1', type: 'temperature_sensor' },
                data: {
                    value: simState.temperature,
                    unit: 'Â°F',
                    dataType: 'number'
                },
                timestamp: new Date().toISOString()
            }
        });
    } else {
        simState.oscillation_count = 0;
    }
}

// Pattern 5: Normal sensor readings
if (Math.random() < 0.5) {
    simState.pressure = 14.5 + (Math.random() - 0.5) * 0.2;
    events.push({
        payload: {
            equipment: { id: 'simulated_pressure_1', type: 'pressure_sensor' },
            data: {
                value: simState.pressure,
                unit: 'PSI',
                dataType: 'number'
            },
            timestamp: new Date().toISOString()
        }
    });
}

// Save state
flow.set('simulation_state', simState);

// Debug logging
if (debugConfig.enabled && debugConfig.eventSettings.showEventFlow) {
    node.status({fill:"blue",shape:"dot",text:`Generated ${events.length} events`});
}

return events.length > 0 ? events : null;

// Safety check added by fix script
const config = msg.config || {};
if (!config.simulateEvents) return null;

// Only proceed if simulation is enabled
const debugConfig = flow.get('debugConfig') || {};

if (debugConfig.eventSettings && debugConfig.(eventSettings && eventSettings.simulateEvents)) {
    return msg;
}

return null;

// Universal event detection for all data sources
const debugConfig = flow.get('debugConfig') || {};
const data = msg.payload;
const equipmentId = data.equipment?.id || 'unknown';
const timestamp = new Date(data.timestamp);

// Get or initialize equipment state
let equipmentStates = flow.get('equipment_states') || {};
if (!equipmentStates[equipmentId]) {
    equipmentStates[equipmentId] = {
        lastValue: null,
        lastState: null,
        lastChange: null,
        deadbandBuffer: null,
        alarmStates: {},
        statistics: {
            changes: 0,
            events: 0,
            alarms: 0
        }
    };
}

const state = equipmentStates[equipmentId];
const events = [];

// Update statistics
let stats = flow.get('event_stats') || {};
stats.totalEvents = (stats.totalEvents || 0) + 1;

// 1. Change of State Detection
if (data.data.dataType === 'boolean' || data.data.dataType === 'string') {
    if (state.lastValue !== null && state.lastValue !== data.data.value) {
        events.push({
            type: 'state_change',
            equipment: equipmentId,
            previousState: state.lastValue,
            currentState: data.data.value,
            timestamp: timestamp,
            duration: state.lastChange ? timestamp - state.lastChange : null
        });
        state.statistics.changes++;
        stats.stateChanges = (stats.stateChanges || 0) + 1;
    }
    state.lastValue = data.data.value;
    state.lastChange = timestamp;
}

// 2. Analog Value Change with Deadband
if (data.data.dataType === 'number' || data.data.dataType === 'float') {
    const value = parseFloat(data.data.value);
    const deadband = getDeadband(equipmentId, data.equipment.type);

    if (state.lastValue !== null) {
        const change = Math.abs(value - state.lastValue);

        // Check if change exceeds deadband
        if (change > deadband) {
            // Check if we're in deadband buffer
            if (!state.deadbandBuffer) {
                state.deadbandBuffer = {
                    startValue: state.lastValue,
                    startTime: state.lastChange,
                    peakValue: value,
                    samples: 1
                };
            } else {
                state.deadbandBuffer.samples++;
                if (Math.abs(value - state.deadbandBuffer.startValue) > Math.abs(state.deadbandBuffer.peakValue - state.deadbandBuffer.startValue)) {
                    state.deadbandBuffer.peakValue = value;
                }
            }

            // Confirm change after 3 samples
            if (state.deadbandBuffer.samples >= 3) {
                events.push({
                    type: 'value_change',
                    equipment: equipmentId,
                    previousValue: state.deadbandBuffer.startValue,
                    currentValue: value,
                    change: value - state.deadbandBuffer.startValue,
                    changePercent: ((value - state.deadbandBuffer.startValue) / state.deadbandBuffer.startValue) * 100,
                    timestamp: timestamp,
                    deadband: deadband
                });
                state.lastValue = value;
                state.lastChange = timestamp;
                state.deadbandBuffer = null;
                state.statistics.changes++;
                stats.valueChanges = (stats.valueChanges || 0) + 1;
            }
        } else {
            // Within deadband - reset buffer
            state.deadbandBuffer = null;
        }
    } else {
        // First value
        state.lastValue = value;
        state.lastChange = timestamp;
    }

    // 3. Threshold Crossing Detection
    const thresholds = getThresholds(equipmentId, data.equipment.type);
    if (thresholds) {
        // High-High Alarm
        if (thresholds.highHigh && value >= thresholds.highHigh) {
            if (!state.alarmStates.highHigh) {
                events.push({
                    type: 'alarm',
                    subtype: 'high_high',
                    equipment: equipmentId,
                    value: value,
                    threshold: thresholds.highHigh,
                    timestamp: timestamp,
                    priority: 'critical'
                });
                state.alarmStates.highHigh = true;
                state.statistics.alarms++;
                stats.alarms = (stats.alarms || 0) + 1;
            }
        } else if (state.alarmStates.highHigh && value < (thresholds.highHigh - (thresholds.deadband || 0))) {
            events.push({
                type: 'alarm_clear',
                subtype: 'high_high',
                equipment: equipmentId,
                value: value,
                timestamp: timestamp
            });
            state.alarmStates.highHigh = false;
        }

        // High Alarm
        if (thresholds.high && value >= thresholds.high && !state.alarmStates.highHigh) {
            if (!state.alarmStates.high) {
                events.push({
                    type: 'alarm',
                    subtype: 'high',
                    equipment: equipmentId,
                    value: value,
                    threshold: thresholds.high,
                    timestamp: timestamp,
                    priority: 'high'
                });
                state.alarmStates.high = true;
                state.statistics.alarms++;
                stats.alarms = (stats.alarms || 0) + 1;
            }
        } else if (state.alarmStates.high && value < (thresholds.high - (thresholds.deadband || 0))) {
            events.push({
                type: 'alarm_clear',
                subtype: 'high',
                equipment: equipmentId,
                value: value,
                timestamp: timestamp
            });
            state.alarmStates.high = false;
        }

        // Low Alarm
        if (thresholds.low && value <= thresholds.low && !state.alarmStates.lowLow) {
            if (!state.alarmStates.low) {
                events.push({
                    type: 'alarm',
                    subtype: 'low',
                    equipment: equipmentId,
                    value: value,
                    threshold: thresholds.low,
                    timestamp: timestamp,
                    priority: 'high'
                });
                state.alarmStates.low = true;
                state.statistics.alarms++;
                stats.alarms = (stats.alarms || 0) + 1;
            }
        } else if (state.alarmStates.low && value > (thresholds.low + (thresholds.deadband || 0))) {
            events.push({
                type: 'alarm_clear',
                subtype: 'low',
                equipment: equipmentId,
                value: value,
                timestamp: timestamp
            });
            state.alarmStates.low = false;
        }

        // Low-Low Alarm
        if (thresholds.lowLow && value <= thresholds.lowLow) {
            if (!state.alarmStates.lowLow) {
                events.push({
                    type: 'alarm',
                    subtype: 'low_low',
                    equipment: equipmentId,
                    value: value,
                    threshold: thresholds.lowLow,
                    timestamp: timestamp,
                    priority: 'critical'
                });
                state.alarmStates.lowLow = true;
                state.statistics.alarms++;
                stats.alarms = (stats.alarms || 0) + 1;
            }
        } else if (state.alarmStates.lowLow && value > (thresholds.lowLow + (thresholds.deadband || 0))) {
            events.push({
                type: 'alarm_clear',
                subtype: 'low_low',
                equipment: equipmentId,
                value: value,
                timestamp: timestamp
            });
            state.alarmStates.lowLow = false;
        }
    }
}

// 4. Rate of Change Detection
if (state.lastValue !== null && state.lastChange !== null && (data.data.dataType === 'number' || data.data.dataType === 'float')) {
    const timeDiff = (timestamp - state.lastChange) / 1000; // seconds
    if (timeDiff > 0) {
        const rateOfChange = (data.data.value - state.lastValue) / timeDiff;
        const rateThreshold = getRateThreshold(equipmentId, data.equipment.type);

        if (rateThreshold && Math.abs(rateOfChange) > rateThreshold) {
            events.push({
                type: 'rate_change',
                equipment: equipmentId,
                rate: rateOfChange,
                threshold: rateThreshold,
                unit: `${data.data.unit}/s`,
                timestamp: timestamp,
                priority: 'warning'
            });
        }
    }
}

// Save state and stats
equipmentStates[equipmentId] = state;
flow.set('equipment_states', equipmentStates);
flow.set('event_stats', stats);

// Debug logging
if (debugConfig.enabled && debugConfig.eventSettings.logAllEvents && events.length > 0) {
    node.warn(`Events detected: ${events.map(e => e.type).join(', ')} for ${equipmentId}`);
}

// Send events if any detected
if (events.length > 0) {
    state.statistics.events += events.length;
    msg.events = events;
    msg.originalData = data;
    return msg;
}

return null;

// Helper functions
function getDeadband(equipmentId, equipmentType) {
    const deadbands = flow.get('deadband_config') || {};
    return deadbands[equipmentId] || getDefaultDeadband(equipmentType);
}

function getDefaultDeadband(equipmentType) {
    const defaults = {
        'temperature_sensor': 0.5,
        'pressure_sensor': 1.0,
        'flow_meter': 2.0,
        'level_sensor': 0.5,
        'analog_input': 0.1
    };
    return defaults[equipmentType] || 0.5;
}

function getThresholds(equipmentId, equipmentType) {
    const thresholds = flow.get('threshold_config') || {};
    return thresholds[equipmentId] || null;
}

function getRateThreshold(equipmentId, equipmentType) {
    const rateThresholds = flow.get('rate_threshold_config') || {};
    return rateThresholds[equipmentId] || getDefaultRateThreshold(equipmentType);
}

function getDefaultRateThreshold(equipmentType) {
    const defaults = {
        'temperature_sensor': 5.0,  // 5 degrees/second
        'pressure_sensor': 10.0,    // 10 PSI/second
        'flow_meter': 50.0,         // 50 GPM/second
        'level_sensor': 5.0         // 5%/second
    };
    return defaults[equipmentType] || null;
}

// Buffer events for batch processing and correlation
const debugConfig = flow.get('debugConfig') || {};
const events = msg.events;
const bufferConfig = flow.get('buffer_config') || {
    maxSize: 1000,
    maxAge: 60000, // 1 minute
    flushInterval: 5000 // 5 seconds
};

// Get or initialize buffer
let eventBuffer = flow.get('event_buffer') || {
    events: [],
    lastFlush: Date.now()
};

// Add events to buffer with metadata
events.forEach(event => {
    eventBuffer.events.push({
        ...event,
        id: generateEventId(),
        bufferedAt: Date.now(),
        processed: false
    });
});

// Check buffer conditions
const now = Date.now();
const shouldFlush = 
    eventBuffer.events.length >= bufferConfig.maxSize ||
    (now - eventBuffer.lastFlush) >= bufferConfig.flushInterval ||
    events.some(e => e.priority === 'critical');

// Debug: Show buffer stats
if (debugConfig.enabled && debugConfig.eventSettings.showBufferStats) {
    const bufferStats = {
        size: eventBuffer.events.length,
        unprocessed: eventBuffer.events.filter(e => !e.processed).length,
        age: now - eventBuffer.lastFlush,
        willFlush: shouldFlush
    };
    node.status({fill:"blue",shape:"ring",text:`Buffer: ${bufferStats.unprocessed}/${bufferStats.size} events`});
}

if (shouldFlush) {
    // Prepare batch for processing
    const batch = eventBuffer.events.filter(e => !e.processed);

    // Mark as processed
    eventBuffer.events.forEach(e => e.processed = true);

    // Clean old events
    eventBuffer.events = eventBuffer.events.filter(e => 
        (now - e.bufferedAt) < bufferConfig.maxAge
    );

    eventBuffer.lastFlush = now;
    flow.set('event_buffer', eventBuffer);

    // Send batch
    msg.payload = {
        batchId: generateBatchId(),
        timestamp: new Date().toISOString(),
        eventCount: batch.length,
        events: batch
    };

    return msg;
}

// Save buffer
flow.set('event_buffer', eventBuffer);

// For critical events, send immediately
const criticalEvents = events.filter(e => e.priority === 'critical');
if (criticalEvents.length > 0) {
    msg.payload = {
        batchId: generateBatchId(),
        timestamp: new Date().toISOString(),
        eventCount: criticalEvents.length,
        events: criticalEvents,
        priority: 'critical'
    };
    return msg;
}

return null;

// Helper functions
function generateEventId() {
    return `evt_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
}

function generateBatchId() {
    return `batch_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`;
}

// Correlate related events to reduce noise and identify patterns
const debugConfig = flow.get('debugConfig') || {};
const batch = msg.payload;
const correlationWindow = 10000; // 10 seconds

// Get correlation rules
const correlationRules = flow.get('correlation_rules') || [
    {
        name: 'pump_trip',
        pattern: [
            { type: 'alarm', equipment: /pump/i, subtype: 'high' },
            { type: 'state_change', equipment: /pump/i, currentState: 'stopped' }
        ],
        window: 5000,
        action: 'create_incident'
    },
    {
        name: 'cascade_failure',
        pattern: [
            { type: 'alarm', count: 3, window: 2000 }
        ],
        action: 'escalate'
    },
    {
        name: 'oscillation',
        pattern: [
            { type: 'value_change', equipment: 'same', minCount: 5, window: 30000 }
        ],
        action: 'suppress'
    }
];

// Sort events by timestamp
const sortedEvents = batch.events.sort((a, b) => 
    new Date(a.timestamp) - new Date(b.timestamp)
);

// Correlation results
const correlatedEvents = [];
const suppressedEvents = [];
const incidents = [];

// Update statistics
let stats = flow.get('event_stats') || {};

// Check each rule
correlationRules.forEach(rule => {
    if (rule.name === 'oscillation' && debugConfig.eventSettings.suppressionEnabled) {
        // Detect oscillating values
        const equipmentGroups = {};
        sortedEvents.forEach(event => {
            if (event.type === 'value_change') {
                if (!equipmentGroups[event.equipment]) {
                    equipmentGroups[event.equipment] = [];
                }
                equipmentGroups[event.equipment].push(event);
            }
        });

        Object.entries(equipmentGroups).forEach(([equipment, events]) => {
            if (events.length >= rule.pattern[0].minCount) {
                const timeSpan = new Date(events[events.length - 1].timestamp) - new Date(events[0].timestamp);
                if (timeSpan <= rule.pattern[0].window) {
                    // Oscillation detected
                    correlatedEvents.push({
                        type: 'correlated_event',
                        subtype: 'oscillation',
                        equipment: equipment,
                        eventCount: events.length,
                        frequency: events.length / (timeSpan / 1000),
                        startTime: events[0].timestamp,
                        endTime: events[events.length - 1].timestamp,
                        action: 'investigate_control_loop'
                    });

                    // Suppress individual events
                    events.forEach(e => suppressedEvents.push(e.id));
                    stats.correlated = (stats.correlated || 0) + 1;
                    stats.suppressed = (stats.suppressed || 0) + events.length;
                }
            }
        });
    }

    if (rule.name === 'pump_trip') {
        // Look for pump trip pattern
        const pumpAlarms = sortedEvents.filter(e => 
            e.type === 'alarm' && e.equipment.includes('pump')
        );

        pumpAlarms.forEach(alarm => {
            const stateChange = sortedEvents.find(e => 
                e.type === 'state_change' &&
                e.equipment === alarm.equipment &&
                e.currentState === 'stopped' &&
                Math.abs(new Date(e.timestamp) - new Date(alarm.timestamp)) <= rule.window
            );

            if (stateChange) {
                incidents.push({
                    type: 'incident',
                    subtype: 'pump_trip',
                    equipment: alarm.equipment,
                    timestamp: alarm.timestamp,
                    severity: 'high',
                    relatedEvents: [alarm.id, stateChange.id],
                    description: `Pump tripped on ${alarm.subtype} alarm`
                });
                stats.incidents = (stats.incidents || 0) + 1;
            }
        });
    }
});

// Filter out suppressed events
const filteredEvents = sortedEvents.filter(e => 
    !suppressedEvents.includes(e.id)
);

// Add noise reduction statistics
const noiseReduction = {
    originalCount: sortedEvents.length,
    filteredCount: filteredEvents.length,
    suppressedCount: suppressedEvents.length,
    correlatedCount: correlatedEvents.length,
    incidentCount: incidents.length,
    reductionPercent: sortedEvents.length > 0 ? 
        ((suppressedEvents.length / sortedEvents.length) * 100).toFixed(1) : 0
};

// Save stats
flow.set('event_stats', stats);

// Debug logging
if (debugConfig.enabled && debugConfig.eventSettings.showCorrelations) {
    if (correlatedEvents.length > 0 || incidents.length > 0) {
        node.warn(`Correlations: ${correlatedEvents.length}, Incidents: ${incidents.length}, Suppressed: ${suppressedEvents.length}`);
    }
    node.status({fill:"green",shape:"dot",text:`Processed ${sortedEvents.length} events, ${noiseReduction.reductionPercent}% reduced`});
}

// Prepare output
msg.payload = {
    batchId: batch.batchId,
    timestamp: batch.timestamp,
    events: filteredEvents,
    correlatedEvents: correlatedEvents,
    incidents: incidents,
    statistics: noiseReduction
};

return msg;

// Ensure proper event sequencing and timestamp management
const debugConfig = flow.get('debugConfig') || {};
