];

// Sort by timestamp and assign sequence numbers
const sequencedEvents = allEvents
    .sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp))
    .map(event => {
        sequenceTracker.lastSequence++;

        // Check for out-of-order events
        if (sequenceTracker.lastTimestamp && 
            new Date(event.timestamp) < new Date(sequenceTracker.lastTimestamp)) {
            sequenceTracker.outOfOrderCount++;
            event.outOfOrder = true;
        }

        sequenceTracker.lastTimestamp = event.timestamp;

        return {
            ...event,
            sequence: sequenceTracker.lastSequence,
            processedAt: new Date().toISOString()
        };
    });

// Save sequence tracker
flow.set('sequence_tracker', sequenceTracker);

