const eventsByType = {
    alarms: sequencedEvents.filter(e => e.type === 'alarm'),
    stateChanges: sequencedEvents.filter(e => e.type === 'state_change'),
    valueChanges: sequencedEvents.filter(e => e.type === 'value_change'),
