        },
        topic: 'events/correlated'
    });
}

// Add batch summary
messages.push({
    payload: {
        batchId: data.batchId,
        totalEvents: sequencedEvents.length,
        statistics: {
            ...data.statistics,
            outOfOrderEvents: sequenceTracker.outOfOrderCount
        },
