    },
    topic: 'events/summary'
});

// Debug status
if (debugConfig.enabled) {
    node.status({fill:"green",shape:"dot",text:`Seq: ${sequenceTracker.lastSequence}, Out of order: ${sequenceTracker.outOfOrderCount}`});
}

return [messages];

