function logEvent(eventMsg) {
    // Get or initialize event log
    let eventLog = flow.get('event_log') || [];

    const logEntry = {
        timestamp: new Date().toISOString(),
        topic: eventMsg.topic,
        payloadSize: JSON.stringify(eventMsg.payload).length,
        mode: debugConfig.mode
    };

    // Extract event details if available
    try {
        const payload = typeof eventMsg.payload === 'string' ? 
            JSON.parse(eventMsg.payload) : eventMsg.payload;

        if (payload.events) {
            logEntry.eventCount = payload.events.length;
            logEntry.eventTypes = [...new Set(payload.events.map(e => e.type))];
        } else if (payload.type) {
            logEntry.eventType = payload.type;
            logEntry.eventCount = payload.count || 1;
        }
    } catch (e) {
        // Ignore parse errors
    }

    eventLog.push(logEntry);

    // Keep last 1000 entries
    if (eventLog.length > 1000) {
        eventLog = eventLog.slice(-1000);
    }

    flow.set('event_log', eventLog);

    // Debug logging
    if (debugConfig.enabled && debugConfig.eventSettings.logAllEvents) {
        node.warn(`Logged: ${logEntry.topic} - ${logEntry.eventCount || 0} events`);
    }
}

return null;

