switch(action) {
    case 'start':
        // Enable Modbus polling
        flow.set('modbus_enabled', true);
        flow.set('modbus_config', config);

        // Initialize device list if not exists
        if (!flow.get('modbus_devices')) {
            flow.set('modbus_devices', {});
        }

        msg.payload = true;
        msg.topic = 'modbus/polling/start';

        // Update module status
        node.send([null, {
            payload: {
                action: 'status_update',
                protocol: 'modbus',
                status: 'active',
                stats: flow.get('modbus_stats') || { messages: 0, errors: 0 },
                debug: debugConfig
            }
        }]);
        break;

    case 'stop':
        // Disable Modbus polling
        flow.set('modbus_enabled', false);

        msg.payload = false;
        msg.topic = 'modbus/polling/stop';

        // Update module status
        node.send([null, {
            payload: {
                action: 'status_update',
                protocol: 'modbus',
                status: 'disabled'
            }
        }]);
        break;

    case 'reconfigure':
        flow.set('modbus_config', config);

        // Reinitialize devices with new config
        msg.payload = config;
        msg.topic = 'modbus/reconfigure';
        break;

    case 'add_device':
        // Add a new Modbus device
        const devices = flow.get('modbus_devices') || {};
        const device = msg.payload.device;

        devices[device.id] = {
            ...device,
            status: 'configured',
            lastSeen: null,
            stats: { reads: 0, errors: 0 }
        };

        flow.set('modbus_devices', devices);
        msg.topic = 'modbus/device/added';
