}

const config = flow.get('modbus_config') || {};
const subnet = config.subnet || '192.168.1';
const startIP = config.startIP || 1;
const endIP = config.endIP || 254;
const commonPorts = config.ports || [502];
const unitIds = config.unitIds || [1, 2, 3, 4, 5];

// In simulation mode, return pre-discovered devices
if (debugConfig.(modbusSettings && modbusSettings.simulateDevices)) {
    const simulatedDiscovery = [
        {
            id: 'discovered_plc_2',
            ip: '192.168.1.102',
            port: 502,
            unitId: 3,
            discoveredAt: new Date(),
            type: 'modbus_tcp',
            status: 'discovered',
            deviceType: 'Siemens S7-1200'
        },
        {
            id: 'discovered_meter_1',
            ip: '192.168.1.110',
            port: 502,
            unitId: 1,
            discoveredAt: new Date(),
            type: 'modbus_tcp',
            status: 'discovered',
            deviceType: 'Power Meter'
        }
    ];

    msg.payload = {
        action: 'scan_complete',
        discovered: simulatedDiscovery,
        simulated: true
    };
    return [null, msg];
}

// Generate scan targets for real discovery
const scanTargets = [];

for (let i = startIP; i <= endIP && i <= startIP + 10; i++) {  // Limit scan range in testing
    const ip = `${subnet}.${i}`;
    commonPorts.forEach(port => {
        unitIds.forEach(unitId => {
            scanTargets.push({
                ip: ip,
                port: port,
                unitId: unitId,
                timeout: debugConfig.modbusSettings.scanTimeout
            });
        });
    });
}

// Store scan progress
flow.set('modbus_scan_targets', scanTargets);
flow.set('modbus_scan_index', 0);
flow.set('modbus_discovered', []);

msg.payload = {
    action: 'start_scan',
    totalTargets: scanTargets.length,
    mode: debugConfig.mode
};

// Debug status
if (debugConfig.enabled) {
    node.status({fill:"yellow",shape:"ring",text:`Scanning ${scanTargets.length} targets`});
}

return [msg, null];

// Scan next device in the list
const targets = flow.get('modbus_scan_targets') || [];
const index = flow.get('modbus_scan_index') || 0;

if (index >= targets.length) {
    // Scan complete
    const discovered = flow.get('modbus_discovered') || [];
    msg.payload = {
        action: 'scan_complete',
        discovered: discovered,
        simulated: false
    };
    msg.topic = 'modbus/discovery/complete';
    return [null, msg];
}

// Get next target
