// Handle Modbus communication errors
const debugConfig = flow.get('debugConfig') || {};
const error = msg.error || msg.payload;
const device = msg.modbusDevice;
const deviceId = device?.id || 'unknown';

// Update error statistics
let stats = flow.get('modbus_stats') || { messages: 0, errors: 0, devices: {} };
stats.errors++;
if (!stats.devices[deviceId]) {
    stats.devices[deviceId] = { reads: 0, errors: 0 };
}
stats.devices[deviceId].errors++;
stats.lastError = {
    device: deviceId,
    message: error.message || 'Unknown error',
    timestamp: new Date(),
    mode: debugConfig.mode
};
flow.set('modbus_stats', stats);

// Update device status
let devices = flow.get('modbus_devices') || {};
if (devices[deviceId]) {
    devices[deviceId].status = 'error';
    devices[deviceId].lastError = error.message;
    devices[deviceId].stats.errors++;
    flow.set('modbus_devices', devices);
}

// Log error based on mode
if (debugConfig.mode === 'production') {
    node.error(`Modbus Error: ${deviceId} - ${error.message}`, msg);
} else if (debugConfig.enabled) {
    node.warn(`Modbus Error (${debugConfig.mode}): ${deviceId} - ${error.message}`);
}

// Send error notification
msg.payload = {
    protocol: 'modbus',
    device: deviceId,
    error: error.message || 'Communication error',
    timestamp: new Date(),
    severity: debugConfig.mode === 'testing' ? 'info' : 'warning'
};
msg.topic = 'protocol/error';

return msg;

// View register maps for debugging
const debugConfig = flow.get('debugConfig') || {};
const devices = flow.get('modbus_devices') || {};

if (!debugConfig.modbusSettings.showRegisterMaps) {
    return null;
}

const registerMaps = {};

Object.values(devices).forEach(device => {
    registerMaps[device.id] = {
        name: device.name,
        type: device.type,
        registers: device.registers.map(reg => ({
            address: reg.address,
            type: reg.type,
            description: reg.description,
            unit: reg.unit,
            dataType: reg.dataType,
            scaling: reg.scaling
        }))
    };
});

msg.payload = registerMaps;
return msg;

// Analyze MQTT topic patterns for integration planning
const catalog = flow.get('mqtt_catalog') || {};
const frequencies = flow.get('mqtt_frequencies') || {};

// Pattern detection
const patterns = {
    hierarchical: [],
    flat: [],
    command: [],
    telemetry: [],
    event: []
};

// Equipment type detection
const equipmentTypes = {};
const parameterTypes = {};

// Analyze each topic
for (const [topic, entry] of Object.entries(catalog)) {
    const parts = topic.split('/');

    // Detect hierarchy patterns
    if (parts.length > 2) {
        patterns.hierarchical.push({
            topic: topic,
            depth: parts.length,
            pattern: parts.slice(0, -1).join('/') + '/*'
        });
    } else {
        patterns.flat.push(topic);
    }

    // Categorize by type
    if (entry.category === 'control') {
        patterns.command.push({
            topic: topic,
            equipment: entry.equipment,
            command: entry.parameter
        });
    } else if (entry.category === 'telemetry') {
        patterns.telemetry.push({
            topic: topic,
            equipment: entry.equipment,
            parameter: entry.parameter,
            dataType: entry.payload.type,
            range: entry.range
        });
    } else if (entry.category === 'alarm' || frequencies[topic]?.pattern === 'event-based') {
        patterns.event.push({
            topic: topic,
            equipment: entry.equipment,
            event: entry.parameter
        });
    }

    // Track equipment types
    if (entry.equipment) {
        if (!equipmentTypes[entry.equipment]) {
            equipmentTypes[entry.equipment] = {
                name: entry.equipment,
                topics: [],
                parameters: new Set(),
                hasControl: false,
                hasTelemetry: false,
                hasEvents: false
            };
        }

        equipmentTypes[entry.equipment].topics.push(topic);
        equipmentTypes[entry.equipment].parameters.add(entry.parameter);

        if (entry.category === 'control') equipmentTypes[entry.equipment].hasControl = true;
        if (entry.category === 'telemetry') equipmentTypes[entry.equipment].hasTelemetry = true;
        if (entry.category === 'alarm') equipmentTypes[entry.equipment].hasEvents = true;
    }

    // Track parameter types
    if (entry.parameter) {
        if (!parameterTypes[entry.parameter]) {
            parameterTypes[entry.parameter] = {
                name: entry.parameter,
                equipment: [],
                dataTypes: new Set(),
                categories: new Set()
            };
        }

        parameterTypes[entry.parameter].equipment.push(entry.equipment);
        parameterTypes[entry.parameter].dataTypes.add(entry.payload.type);
        parameterTypes[entry.parameter].categories.add(entry.category);
    }
}

// Convert sets to arrays
for (const eq of Object.values(equipmentTypes)) {
    eq.parameters = Array.from(eq.parameters);
}

for (const param of Object.values(parameterTypes)) {
    param.dataTypes = Array.from(param.dataTypes);
    param.categories = Array.from(param.categories);
}

// Generate analysis report
const analysis = {
    timestamp: new Date().toISOString(),
    summary: {
        totalTopics: Object.keys(catalog).length,
        hierarchicalTopics: patterns.hierarchical.length,
        flatTopics: patterns.flat.length,
        commandTopics: patterns.command.length,
        telemetryTopics: patterns.telemetry.length,
        eventTopics: patterns.event.length,
        uniqueEquipment: Object.keys(equipmentTypes).length,
        uniqueParameters: Object.keys(parameterTypes).length
    },
    patterns: patterns,
    equipmentTypes: equipmentTypes,
    parameterTypes: parameterTypes
};

// Store analysis
flow.set('mqtt_analysis', analysis);

msg.payload = analysis;
return msg;

// Generate recommendations for Ignition integration
const analysis = msg.payload;
const recommendations = [];

// Analyze equipment for UDT candidates
for (const [name, equipment] of Object.entries(analysis.equipmentTypes)) {
    if (equipment.parameters.length >= 3) {
        recommendations.push({
            type: 'UDT_CANDIDATE',
            priority: 'high',
            equipment: name,
            reason: `Has ${equipment.parameters.length} parameters - good candidate for UDT`,
            parameters: equipment.parameters,
            action: 'Create UDT in Ignition with these parameters'
        });
    }
}

// Identify command/control patterns
if (analysis.patterns.command.length > 0) {
    const commandEquipment = new Set(analysis.patterns.command.map(c => c.equipment));
    recommendations.push({
        type: 'CONTROL_INTEGRATION',
        priority: 'high',
        equipment: Array.from(commandEquipment),
        reason: `Found ${analysis.patterns.command.length} control topics`,
        action: 'Set up bidirectional MQTT communication for control'
    });
}

// High-frequency data recommendations
const frequencies = flow.get('mqtt_frequencies') || {};
const highFreqTopics = Object.entries(frequencies)
    .filter(([topic, freq]) => freq.pattern === 'high-frequency' || freq.pattern === 'real-time')
    .map(([topic]) => topic);

if (highFreqTopics.length > 0) {
    recommendations.push({
        type: 'PERFORMANCE',
        priority: 'medium',
        topics: highFreqTopics.slice(0, 5), // Show first 5
        reason: `Found ${highFreqTopics.length} high-frequency topics`,
        action: 'Consider using MQTT Transmission for efficient data transfer'
    });
}

// Event-based integration
if (analysis.patterns.event.length > 0) {
    recommendations.push({
        type: 'EVENT_HANDLING',
        priority: 'medium',
        events: analysis.patterns.event.slice(0, 5),
        reason: `Found ${analysis.patterns.event.length} event-based topics`,
        action: 'Configure alarm pipeline in Ignition for these events'
    });
}

// Topic naming recommendations
const inconsistentNaming = [];
for (const topic of Object.keys(analysis.patterns.hierarchical)) {
    const parts = topic.split('/');
    if (parts.some(part => part.includes(' ') || part.includes('-'))) {
        inconsistentNaming.push(topic);
    }
}

if (inconsistentNaming.length > 0) {
    recommendations.push({
        type: 'NAMING_CONVENTION',
        priority: 'low',
        topics: inconsistentNaming.slice(0, 5),
        reason: 'Found inconsistent topic naming (spaces, hyphens)',
        action: 'Consider standardizing to underscore_separated names'
    });
}

// Store recommendations
flow.set('integration_recommendations', recommendations);

msg.payload = recommendations;
return msg;

// Create comprehensive integration plan
const analysis = flow.get('mqtt_analysis') || {};
const recommendations = flow.get('integration_recommendations') || [];
const catalog = flow.get('mqtt_catalog') || {};

// Build integration plan
const plan = {
