        equipment: [],
        topics: []
    },

    proposedUDTs: [],

    mqttConfiguration: {
        broker: 'localhost:1883',
        topicStructure: 'analyzed',
        publishRate: 'varies by equipment',
        qos: 0
    },

    ignitionConfiguration: {
