        tagStructure: {
            root: 'Enterprise/Brewery',
            registration: 'Registration/Data',
            data: 'Enterprise/Brewery/Data',
            config: 'Enterprise/Brewery/Config'
        }
    },

    implementationSteps: [],

    recommendations: recommendations
};

// Process equipment for UDTs
for (const [name, equipment] of Object.entries(analysis.equipmentTypes || {})) {
    if (equipment.parameters.length >= 3) {
        // Create UDT definition
        const udtDef = {
            name: name.replace(/[^a-zA-Z0-9]/g, '_') + '_UDT',
            parameters: equipment.parameters.map(param => {
                // Find data type from catalog
                const topic = equipment.topics.find(t => t.includes(param));
                const entry = catalog[topic];
                return {
                    name: param,
                    dataType: entry?.payload.isNumeric ? 'Float' : 'String',
                    mqttTopic: topic
                };
            }),
            instances: [{
                name: name,
                location: 'Unknown' // Would be filled during registration
            }]
        };
        plan.proposedUDTs.push(udtDef);
    }

    // Add to discovered assets
    plan.discoveredAssets.equipment.push({
        name: name,
        topicCount: equipment.topics.length,
        parameters: equipment.parameters,
        hasControl: equipment.hasControl,
        hasTelemetry: equipment.hasTelemetry
    });
}

// Add sample topics
const sampleTopics = Object.entries(catalog).slice(0, 20);
plan.discoveredAssets.topics = sampleTopics.map(([topic, entry]) => ({
    topic: topic,
    category: entry.category,
    messageCount: entry.messageCount,
    lastValue: entry.lastValue
}));

// Implementation steps
plan.implementationSteps = [
    {
        step: 1,
        title: 'Review Discovered MQTT Topics',
        description: 'Validate the discovered equipment and topics match expected brewery assets',
        status: 'completed'
    },
    {
        step: 2,
        title: 'Create UDTs in Ignition',
        description: 'Import the proposed UDT definitions into Ignition Designer',
        status: 'pending'
    },
    {
        step: 3,
        title: 'Configure MQTT Transmission',
        description: 'Set up MQTT modules to bridge Node-RED and Ignition',
        status: 'pending'
    },
    {
        step: 4,
        title: 'Register Equipment',
        description: 'Use the equipment registration flow to create instances',
        status: 'pending'
    },
    {
        step: 5,
        title: 'Map MQTT Topics to Tags',
        description: 'Configure tag mappings for each equipment parameter',
        status: 'pending'
    },
    {
        step: 6,
        title: 'Test Data Flow',
        description: 'Verify data flows from MQTT through to Ignition tags',
        status: 'pending'
    },
    {
        step: 7,
        title: 'Configure Alarms',
        description: 'Set up alarm conditions based on discovered patterns',
        status: 'pending'
    }
];

// Create export
msg.payload = JSON.stringify(plan, null, 2);
msg.filename = `integration_plan_${new Date().toISOString().split('T')[0]}.json`;

return msg;

// Analyze MQTT topic structure and categorize
const topic = msg.topic;
const payload = msg.payload;
const timestamp = new Date();

// Parse topic structure
const topicParts = topic.split('/');
const topicDepth = topicParts.length;

// Identify patterns
let category = 'unknown';
let dataType = 'sensor';
let equipment = '';
let parameter = '';

// Common MQTT topic patterns
// Examples: brewery/cellar/pump1/pressure, control/valve/v1/command
if (topic.includes('control') || topic.includes('command') || topic.includes('set')) {
    category = 'control';
    dataType = 'command';
} else if (topic.includes('status') || topic.includes('state')) {
    category = 'status';
    dataType = 'state';
} else if (topic.includes('alarm') || topic.includes('alert')) {
    category = 'alarm';
    dataType = 'event';
} else if (topic.includes('config') || topic.includes('setting')) {
    category = 'configuration';
    dataType = 'config';
} else {
    category = 'telemetry';
    dataType = 'sensor';
}

// Try to identify equipment and parameter
if (topicDepth >= 3) {
    // Assume pattern like: area/equipment/parameter
    equipment = topicParts[topicParts.length - 2];
    parameter = topicParts[topicParts.length - 1];
} else if (topicDepth === 2) {
    equipment = topicParts[0];
    parameter = topicParts[1];
}

// Analyze payload
let payloadType = typeof payload;
let payloadSize = 0;
let isNumeric = false;
let range = { min: null, max: null };

if (payloadType === 'object') {
    payloadSize = JSON.stringify(payload).length;
} else if (payloadType === 'string') {
    payloadSize = payload.length;
    // Check if string is numeric
    if (!isNaN(payload)) {
        isNumeric = true;
        payloadType = 'numeric_string';
    }
} else if (payloadType === 'number') {
    isNumeric = true;
    payloadSize = payload.toString().length;
}

// Create discovery record
const discovery = {
    topic: topic,
    topicParts: topicParts,
    topicDepth: topicDepth,
    category: category,
    dataType: dataType,
    equipment: equipment,
    parameter: parameter,
    lastSeen: timestamp,
    payload: {
        type: payloadType,
        size: payloadSize,
        isNumeric: isNumeric,
        sample: payload
    }
};

msg.discovery = discovery;
return msg;

// Update the catalog of discovered topics
const discovery = msg.discovery;
const topic = discovery.topic;

// Get or initialize catalog
let catalog = flow.get('mqtt_catalog') || {};

// Update or create entry
if (catalog[topic]) {
    // Update existing entry
    catalog[topic].lastSeen = discovery.lastSeen;
    catalog[topic].messageCount = (catalog[topic].messageCount || 0) + 1;

    // Update value range for numeric data
    if (discovery.payload.isNumeric) {
        const value = parseFloat(msg.payload);
        if (!isNaN(value)) {
            catalog[topic].range = catalog[topic].range || { min: value, max: value };
            catalog[topic].range.min = Math.min(catalog[topic].range.min, value);
            catalog[topic].range.max = Math.max(catalog[topic].range.max, value);
            catalog[topic].lastValue = value;
        }
    }
} else {
    // Create new entry
    catalog[topic] = {
        ...discovery,
        firstSeen: discovery.lastSeen,
        messageCount: 1,
        range: discovery.payload.isNumeric ? { min: parseFloat(msg.payload), max: parseFloat(msg.payload) } : null,
        lastValue: msg.payload
    };
}

// Store updated catalog
flow.set('mqtt_catalog', catalog);

// Send catalog update
msg.payload = {
    action: 'update',
    topic: topic,
    entry: catalog[topic],
    totalTopics: Object.keys(catalog).length
};

return msg;

// Calculate message frequency for each topic
const topic = msg.discovery.topic;
const now = Date.now();

// Get or initialize frequency tracking
let frequencies = flow.get('mqtt_frequencies') || {};

if (!frequencies[topic]) {
    frequencies[topic] = {
        timestamps: [],
        frequency: 0,
        pattern: 'unknown'
    };
}

// Add timestamp
frequencies[topic].timestamps.push(now);

// Keep only last 100 timestamps
if (frequencies[topic].timestamps.length > 100) {
    frequencies[topic].timestamps = frequencies[topic].timestamps.slice(-100);
}

// Calculate frequency if we have enough samples
if (frequencies[topic].timestamps.length >= 5) {
    const timestamps = frequencies[topic].timestamps;
    const intervals = [];

    for (let i = 1; i < timestamps.length; i++) {
        intervals.push(timestamps[i] - timestamps[i-1]);
    }

    // Calculate average interval
    const avgInterval = intervals.reduce((a, b) => a + b, 0) / intervals.length;
    frequencies[topic].frequency = 1000 / avgInterval; // Hz

    // Determine pattern
    if (avgInterval < 100) {
        frequencies[topic].pattern = 'high-frequency';
    } else if (avgInterval < 1000) {
        frequencies[topic].pattern = 'real-time';
    } else if (avgInterval < 10000) {
        frequencies[topic].pattern = 'periodic';
    } else if (avgInterval < 60000) {
        frequencies[topic].pattern = 'slow-periodic';
    } else {
        frequencies[topic].pattern = 'event-based';
    }
}

// Store updated frequencies
flow.set('mqtt_frequencies', frequencies);

msg.frequency = frequencies[topic];
return msg;

// Store raw messages for later analysis
const topic = msg.topic;
const payload = msg.payload;
const timestamp = new Date();

// Get or initialize message store
let messageStore = flow.get('mqtt_messages') || {};

if (!messageStore[topic]) {
    messageStore[topic] = [];
}

// Store message
messageStore[topic].push({
    timestamp: timestamp,
    payload: payload,
    size: JSON.stringify(payload).length
});

// Keep only last 50 messages per topic
if (messageStore[topic].length > 50) {
    messageStore[topic] = messageStore[topic].slice(-50);
}

// Store updated messages
flow.set('mqtt_messages', messageStore);

return null; // Don't pass through

// Apply filters to catalog
const filters = msg.payload;
const catalog = flow.get('mqtt_catalog') || {};

// Filter catalog
const filtered = {};

for (const [topic, entry] of Object.entries(catalog)) {
    // Check category filter
    if (!filters[entry.category]) {
        continue;
    }

    // Check search filter
    if (filters.search && !topic.toLowerCase().includes(filters.search.toLowerCase())) {
        continue;
    }

    filtered[topic] = entry;
}

// Store filtered view
flow.set('filtered_catalog', filtered);

msg.payload = {
    filtered: filtered,
    total: Object.keys(filtered).length,
    original: Object.keys(catalog).length
};

return msg;

// Export discovered equipment inventory
const catalog = flow.get('mqtt_catalog') || {};
const frequencies = flow.get('mqtt_frequencies') || {};

// Build equipment inventory
const equipment = {};
const topics = {};

// Group by equipment
for (const [topic, entry] of Object.entries(catalog)) {
    const equipmentName = entry.equipment || 'unknown';

    if (!equipment[equipmentName]) {
        equipment[equipmentName] = {
            name: equipmentName,
            topics: [],
            parameters: [],
            categories: new Set(),
            messageCount: 0
        };
    }

    equipment[equipmentName].topics.push(topic);
    equipment[equipmentName].parameters.push(entry.parameter);
    equipment[equipmentName].categories.add(entry.category);
    equipment[equipmentName].messageCount += entry.messageCount;

    // Store topic details
    topics[topic] = {
        ...entry,
        frequency: frequencies[topic] || null
    };
}

// Convert sets to arrays
for (const eq of Object.values(equipment)) {
    eq.categories = Array.from(eq.categories);
}

// Create export object
const exportData = {
    metadata: {
        exportDate: new Date().toISOString(),
        totalTopics: Object.keys(catalog).length,
        totalEquipment: Object.keys(equipment).length,
        nodeRedInstance: 'Steel Bonnet Brewery'
    },
    equipment: equipment,
    topics: topics,
    summary: {
        byCategory: {},
        byPattern: {}
    }
};

// Add summaries
for (const entry of Object.values(catalog)) {
    exportData.summary.byCategory[entry.category] = 
        (exportData.summary.byCategory[entry.category] || 0) + 1;
}

for (const freq of Object.values(frequencies)) {
    if (freq.pattern !== 'unknown') {
        exportData.summary.byPattern[freq.pattern] = 
            (exportData.summary.byPattern[freq.pattern] || 0) + 1;
    }
}

// Create download
msg.payload = JSON.stringify(exportData, null, 2);
msg.filename = `mqtt_inventory_${new Date().toISOString().split('T')[0]}.json`;
msg.headers = {
    'Content-Type': 'application/json',
    'Content-Disposition': `attachment; filename="${msg.filename}"`
};

return msg;

// Initialize debug configuration from global settings
const globalDebug = global.get('debugConfig') || {
    enabled: true,
    mode: 'testing',
    showDataFormats: true,
    validateData: false,
    opcEnabled: false,
    simulationEnabled: true
};

// MQTT-specific debug settings
const mqttDebug = {
    ...globalDebug,
    mqttSettings: {
        showTopicPatterns: true,
        logAllMessages: false,
        simulateBroker: globalDebug.simulationEnabled,
        connectionRetries: globalDebug.mode === 'production' ? 5 : 1,
        discoveryInterval: globalDebug.mode === 'testing' ? 5 : 60
    }
};

// Store in flow context
flow.set('debugConfig', mqttDebug);
flow.set('mqtt_enabled', mqttDebug.simulationEnabled || mqttDebug.mode !== 'testing');

// Initialize stats
flow.set('mqtt_stats', {
    messages: 0,
    errors: 0,
    topics: {},
    startTime: new Date(),
    mode: mqttDebug.mode
});

msg.payload = {
    debug: mqttDebug,
    status: 'Debug configuration initialized'
};

return msg;

