// Handle control commands for MQTT module
const debugConfig = flow.get('debugConfig') || {};
const action = msg.payload.action;
const config = msg.payload.config || {};

// Debug: Log control actions
if (debugConfig.enabled) {
    node.warn(`MQTT Control: ${action} in ${debugConfig.mode} mode`);
}

switch(action) {
    case 'start':
        // Enable MQTT discovery
        flow.set('mqtt_enabled', true);
        flow.set('mqtt_config', config);

        // Start discovery
        msg.payload = true;
        msg.topic = 'mqtt/discovery/start';

        // Update module status
        node.send([null, {
            payload: {
                action: 'status_update',
                protocol: 'mqtt',
                status: 'active',
                stats: flow.get('mqtt_stats') || { messages: 0, errors: 0 },
                debug: debugConfig
            }
        }]);
        break;

    case 'stop':
        // Disable MQTT discovery
        flow.set('mqtt_enabled', false);

        msg.payload = false;
        msg.topic = 'mqtt/discovery/stop';

        // Update module status
        node.send([null, {
            payload: {
                action: 'status_update',
                protocol: 'mqtt',
                status: 'disabled'
            }
        }]);
        break;

    case 'reconfigure':
        flow.set('mqtt_config', config);
        msg.payload = config;
        msg.topic = 'mqtt/reconfigure';
        break;

    case 'debug':
        // Toggle debug mode
        debugConfig.enabled = !debugConfig.enabled;
        flow.set('debugConfig', debugConfig);
        msg.payload = { debug: debugConfig };
        break;
}

return [msg, null];

// Only proceed if MQTT module is enabled
const debugConfig = flow.get('debugConfig') || {};
const mqttEnabled = flow.get('mqtt_enabled');

if (mqttEnabled || (debugConfig.mqttSettings && debugConfig.mqttSettings.simulateBroker)) {
    return msg;
}

// Debug: Log skipped discovery
if (debugConfig.enabled) {
    node.status({fill:"grey",shape:"ring",text:"Discovery skipped - module disabled"});
}

return null;

// Check if we should use real broker or simulation
const debugConfig = flow.get('debugConfig') || {};

if (debugConfig.mqttSettings && debugConfig.mqttSettings.simulateBroker) {
