    { topic: 'utilities/compressor/status', value: Math.random() > 0.3 ? 'running' : 'idle' },
    { topic: 'utilities/boiler/temperature', value: 180 + Math.random() * 10 },
    { topic: 'homeassistant/sensor/brewery_ambient/config', value: {
