// Process incoming MQTT messages
const debugConfig = flow.get('debugConfig') || {};
const topic = msg.topic;
const payload = msg.payload;
const isSimulated = msg._simulated || false;

// Skip system topics
if (topic.startsWith('$SYS/')) {
    return null;
}

// Update statistics
let stats = flow.get('mqtt_stats') || { messages: 0, errors: 0, topics: {} };
stats.messages++;
if (!stats.topics[topic]) {
    stats.topics[topic] = {
        count: 0,
        lastValue: null,
        lastSeen: null,
        simulated: isSimulated
    };
}
stats.topics[topic].count++;
stats.topics[topic].lastValue = payload;
stats.topics[topic].lastSeen = new Date();
flow.set('mqtt_stats', stats);

// Parse topic structure
const topicParts = topic.split('/');
let deviceId = 'unknown';
let equipmentName = null;
let parameter = null;

// Try to identify device/equipment from topic
// Common patterns: location/equipment/parameter, equipment/parameter, etc.
if (topicParts.length >= 3) {
    deviceId = topicParts[topicParts.length - 2];
    parameter = topicParts[topicParts.length - 1];
    equipmentName = deviceId;
} else if (topicParts.length === 2) {
    deviceId = topicParts[0];
    parameter = topicParts[1];
    equipmentName = deviceId;
}

// Detect data type
let value = payload;
let dataType = 'string';

if (typeof payload === 'object' && payload !== null) {
    // Handle JSON payloads
    if (payload.value !== undefined) {
        value = payload.value;
    }
    dataType = 'object';
} else if (!isNaN(payload)) {
    value = parseFloat(payload);
    dataType = 'number';
} else if (payload === 'true' || payload === 'false') {
    value = payload === 'true';
    dataType = 'boolean';
}

// Data validation if enabled
if (debugConfig.validateData && debugConfig.mode !== 'testing') {
    // Validate numeric ranges
    if (dataType === 'number' && parameter === 'temperature') {
        if (value < -50 || value > 300) {
            node.warn(`Invalid temperature value: ${value} for ${topic}`);
        }
    }
}

// Create normalized message
const normalizedMsg = {
    payload: {
        deviceId: deviceId,
        equipmentName: equipmentName,
        topic: topic,
        value: value,
        dataType: dataType,
        parameter: parameter,
        raw: payload,
        simulated: isSimulated,
        timestamp: new Date()
    },
    protocol: 'mqtt',
    topic: 'protocol/data'
};

// Debug logging
if (debugConfig.enabled && debugConfig.mqttSettings.logAllMessages) {
    node.warn(`MQTT Message: ${topic} = ${JSON.stringify(value)} (${dataType})`);
}

// Check for discovery patterns
if (topic.includes('/config') || topic.includes('/discovery')) {
    // This might be a device announcing itself
    return [null, normalizedMsg, normalizedMsg];
} else {
    // Regular data message
    return [normalizedMsg, null, normalizedMsg];
}

// Handle MQTT device discovery
const debugConfig = flow.get('debugConfig') || {};
const topic = msg.payload.topic;
const payload = msg.payload.raw;

// Store discovered devices
let discoveredDevices = flow.get('mqtt_discovered_devices') || {};

// Extract device info from discovery message
if (typeof payload === 'object' && payload.device) {
    const device = payload.device;
    const deviceKey = device.identifiers?.[0] || topic.split('/')[1];

    discoveredDevices[deviceKey] = {
        name: device.name,
        manufacturer: device.manufacturer || 'Unknown',
        model: device.model || 'Unknown',
        identifiers: device.identifiers || [deviceKey],
        discoveredAt: new Date(),
        topic: topic,
        capabilities: payload.device_class || [],
        simulated: msg.payload.simulated
    };

    flow.set('mqtt_discovered_devices', discoveredDevices);

    // Send discovery notification
    msg.payload = {
        action: 'device_discovered',
        protocol: 'mqtt',
        device: discoveredDevices[deviceKey]
    };
    msg.topic = 'device/discovery';

    // Debug logging
    if (debugConfig.enabled) {
        node.status({fill:"green",shape:"dot",text:`Discovered: ${device.name}`});
    }

    return msg;
}

return null;

// Analyze MQTT topic patterns for auto-discovery
const debugConfig = flow.get('debugConfig') || {};
const stats = flow.get('mqtt_stats') || {};
const topics = Object.keys(stats.topics || {});

if (topics.length === 0) {
    msg.payload = {
        patterns: {},
        rules: [],
        topicCount: 0,
        message: 'No topics discovered yet'
    };
    return msg;
}

// Analyze topic patterns
const patterns = {
    byDepth: {},
    byPrefix: {},
    commonStructures: [],
    simulated: { count: 0, topics: [] }
};

// Group by depth
topics.forEach(topic => {
    const depth = topic.split('/').length;
    if (!patterns.byDepth[depth]) {
        patterns.byDepth[depth] = [];
    }
    patterns.byDepth[depth].push(topic);

    // Track simulated topics
    if (stats.topics[topic].simulated) {
        patterns.simulated.count++;
        patterns.simulated.topics.push(topic);
    }
});

// Group by prefix
topics.forEach(topic => {
    const prefix = topic.split('/')[0];
    if (!patterns.byPrefix[prefix]) {
        patterns.byPrefix[prefix] = [];
    }
    patterns.byPrefix[prefix].push(topic);
});

// Identify common structures
const structureMap = {};
topics.forEach(topic => {
    const parts = topic.split('/');
    const structure = parts.map((part, index) => {
        // Replace numbers with # and specific names with *
        if (/^\d+$/.test(part)) return '#';
        if (index === parts.length - 1) return 'parameter';
        if (index === parts.length - 2) return 'device';
        return part;
    }).join('/');

    if (!structureMap[structure]) {
        structureMap[structure] = {
            pattern: structure,
            count: 0,
            examples: []
        };
    }
    structureMap[structure].count++;
    if (structureMap[structure].examples.length < 3) {
        structureMap[structure].examples.push(topic);
    }
});

patterns.commonStructures = Object.values(structureMap)
    .filter(s => s.count > 1)
    .sort((a, b) => b.count - a.count);

// Store patterns
flow.set('mqtt_topic_patterns', patterns);

// Generate discovery rules
const rules = patterns.commonStructures.map(structure => {
    const parts = structure.pattern.split('/');
    return {
        pattern: structure.pattern,
        deviceIndex: parts.indexOf('device'),
        parameterIndex: parts.indexOf('parameter'),
        examples: structure.examples
    };
});

flow.set('mqtt_discovery_rules', rules);

msg.payload = {
    patterns: patterns,
    rules: rules,
    topicCount: topics.length,
    mode: debugConfig.mode,
    simulatedRatio: patterns.simulated.count / topics.length
};

// Debug status
if (debugConfig.enabled && debugConfig.mqttSettings.showTopicPatterns) {
    node.status({fill:"blue",shape:"ring",text:`${topics.length} topics, ${patterns.commonStructures.length} patterns`});
}

return msg;

// Store pattern analysis for use by other flows
const patterns = msg.payload;

// Make patterns available globally
global.set('mqtt_topic_patterns', patterns);

// Create summary for status
msg.payload = {
    stored: true,
    topicCount: patterns.topicCount,
    patternCount: patterns.rules.length,
    timestamp: new Date()
};

return msg;

