stats.errors++;
stats.lastError = {
    message: error.message,
    source: error.source?.name || 'unknown',
    timestamp: new Date(),
    mode: debugConfig.mode
};
flow.set('mqtt_stats', stats);

// Log error based on mode
if (debugConfig.mode === 'production') {
    node.error(`MQTT Error: ${error.message}`, msg);
} else if (debugConfig.enabled) {
    node.warn(`MQTT Error (${debugConfig.mode}): ${error.message}`);
}

// Send error notification
msg.payload = {
    protocol: 'mqtt',
    error: error.message,
    timestamp: new Date(),
    severity: debugConfig.mode === 'testing' ? 'info' : 'warning',
    simulated: msg._simulated || false
};
msg.topic = 'protocol/error';

return msg;

// Manage OPC UA connection state and configuration
const action = msg.payload.action || 'status';

// Get or initialize connection state
let connectionState = flow.get('opcua_connection_state') || {
    connected: false,
    endpoint: 'opc.tcp://localhost:62541/discovery',
    securityMode: 'None',
    securityPolicy: 'None',
    connectionAttempts: 0,
    lastError: null,
    lastConnected: null,
    statistics: {
        writes: 0,
        reads: 0,
        errors: 0,
        reconnects: 0
    }
};

switch(action) {
    case 'connect':
        connectionState.connectionAttempts++;
        msg.payload = {
            endpoint: connectionState.endpoint,
            securityMode: connectionState.securityMode,
            securityPolicy: connectionState.securityPolicy
        };
        msg.topic = 'opcua/connect';
        return [msg, null];

    case 'disconnect':
        connectionState.connected = false;
        flow.set('opcua_connection_state', connectionState);
        msg.topic = 'opcua/disconnect';
        return [msg, null];

    case 'connected':
        connectionState.connected = true;
        connectionState.lastConnected = new Date();
        connectionState.lastError = null;
        flow.set('opcua_connection_state', connectionState);

        // Send connection success notification
        msg.payload = {
            status: 'connected',
            endpoint: connectionState.endpoint,
            timestamp: connectionState.lastConnected
        };
        return [null, msg];

    case 'error':
        connectionState.connected = false;
        connectionState.lastError = msg.payload.error || 'Unknown error';
        connectionState.statistics.errors++;
        flow.set('opcua_connection_state', connectionState);

        // Check if we should attempt reconnect
        if (connectionState.connectionAttempts < 5) {
            setTimeout(() => {
                node.send([{
                    payload: { action: 'connect' },
                    topic: 'opcua/control'
                }, null]);
            }, 5000); // Retry after 5 seconds
        }

        return [null, msg];

    case 'status':
        msg.payload = connectionState;
        return [null, msg];
}

return null;

// Map equipment registration data to OPC UA tag paths
const data = msg.payload;
const action = data.action || 'write';

// Get ISA-95 tag structure
function buildTagPath(equipment) {
    // Base path: [default]Enterprise/SteelBonnet/MainBrewery/Area/WorkCenter/Equipment
    const basePath = `[default]Enterprise/${equipment.enterprise}/${equipment.site}/${equipment.area}/${equipment.workCenter}`;

    if (equipment.workUnit) {
        return `${basePath}/${equipment.workUnit}/${equipment.name}`;
    } else {
        return `${basePath}/${equipment.name}`;
    }
}

// Get OPC UA node ID format
function getNodeId(tagPath) {
    // Ignition uses ns=2 for tag provider tags
    return `ns=2;s=${tagPath}`;
