    const equipment = data.equipment;
    const tagPath = buildTagPath(equipment);
    const messages = [];

    // Create folder structure
    const folders = [];
    const pathParts = tagPath.split('/');
    for (let i = 1; i < pathParts.length; i++) {
        folders.push(pathParts.slice(0, i + 1).join('/'));
    }

    // Create folder messages
    folders.forEach(folder => {
        messages.push({
            payload: {
                action: 'addFolder',
                folder: {
                    nodeId: getNodeId(folder),
                    browseName: folder.split('/').pop()
                }
            },
            topic: 'opcua/folder/create'
        });
    });

    // Create equipment tags
    const tags = [
        // Metadata tags
        {
            nodeId: getNodeId(`${tagPath}/Meta/Type`),
            value: equipment.equipmentType,
            dataType: 'String'
        },
        {
            nodeId: getNodeId(`${tagPath}/Meta/Description`),
            value: equipment.description,
            dataType: 'String'
        },
        {
            nodeId: getNodeId(`${tagPath}/Meta/Location`),
            value: equipment.isa95Path,
            dataType: 'String'
        },
        {
            nodeId: getNodeId(`${tagPath}/Meta/RegisteredAt`),
            value: equipment.registeredAt,
