            value: new Date(),
            dataType: 'DateTime'
        },
        {
            nodeId: getNodeId(`${tagPath}/Status/Quality`),
            value: 'Unknown',
            dataType: 'String'
        },

        // Data tags based on equipment type
        {
            nodeId: getNodeId(`${tagPath}/Data/Value`),
