        },
        {
            nodeId: getNodeId(`${tagPath}/Data/Units`),
            value: equipment.units || '',
            dataType: 'String'
        }
    ];

    // Add alarm configuration if present
    if (equipment.alarmHH !== undefined) {
        tags.push({
            nodeId: getNodeId(`${tagPath}/Alarms/HiHi/Setpoint`),
            value: equipment.alarmHH,
            dataType: 'Float'
        });
    }
    if (equipment.alarmH !== undefined) {
        tags.push({
            nodeId: getNodeId(`${tagPath}/Alarms/Hi/Setpoint`),
            value: equipment.alarmH,
            dataType: 'Float'
        });
    }
    if (equipment.alarmL !== undefined) {
        tags.push({
            nodeId: getNodeId(`${tagPath}/Alarms/Lo/Setpoint`),
            value: equipment.alarmL,
            dataType: 'Float'
        });
    }
    if (equipment.alarmLL !== undefined) {
        tags.push({
            nodeId: getNodeId(`${tagPath}/Alarms/LoLo/Setpoint`),
            value: equipment.alarmLL,
            dataType: 'Float'
        });
    }

    // Create tag write messages
    tags.forEach(tag => {
        messages.push({
            payload: {
                action: 'write',
                nodeId: tag.nodeId,
                value: tag.value,
                dataType: tag.dataType
            },
            topic: 'opcua/tag/write'
        });
    });

    // Store mapping
    let mappings = flow.get('equipment_opcua_mappings') || {};
    mappings[equipment.id] = {
        equipmentId: equipment.id,
        tagPath: tagPath,
        nodeIdBase: getNodeId(tagPath),
        registeredAt: new Date()
    };
    flow.set('equipment_opcua_mappings', mappings);

    return [messages];

} else if (action === 'write_data') {
    // Operational data write
    const mappings = flow.get('equipment_opcua_mappings') || {};
    const mapping = mappings[data.equipmentId];

    if (!mapping) {
        node.error(`No OPC mapping found for equipment: ${data.equipmentId}`);
        return null;
    }

    const messages = [];

    // Handle JSON payload
    if (typeof data.value === 'object') {
        Object.entries(data.value).forEach(([key, value]) => {
            messages.push({
                payload: {
                    action: 'write',
                    nodeId: getNodeId(`${mapping.tagPath}/Data/${key}`),
                    value: value,
                    dataType: typeof value === 'number' ? 'Float' : 
                             typeof value === 'boolean' ? 'Boolean' : 'String'
                },
                topic: 'opcua/tag/write'
            });
        });
    } else {
        // Primitive value
        messages.push({
            payload: {
                action: 'write',
                nodeId: getNodeId(`${mapping.tagPath}/Data/Value`),
                value: data.value,
                dataType: data.dataType || 'Float'
            },
            topic: 'opcua/tag/write'
        });
    }

    // Update status tags
    messages.push({
        payload: {
            action: 'write',
            nodeId: getNodeId(`${mapping.tagPath}/Status/Online`),
            value: true,
            dataType: 'Boolean'
        },
        topic: 'opcua/tag/write'
    });

    messages.push({
        payload: {
            action: 'write',
            nodeId: getNodeId(`${mapping.tagPath}/Status/LastUpdate`),
            value: new Date(),
            dataType: 'DateTime'
        },
        topic: 'opcua/tag/write'
    });

    messages.push({
        payload: {
            action: 'write',
            nodeId: getNodeId(`${mapping.tagPath}/Status/Quality`),
            value: data.quality || 'Good',
            dataType: 'String'
