        topic: 'opcua/tag/write'
    });

    return [messages];
}

return null;

// Queue OPC UA write operations for batch processing
const messages = msg.payload;

// Get or initialize write queue
let writeQueue = flow.get('opcua_write_queue') || [];

// Add messages to queue
if (Array.isArray(messages)) {
    messages.forEach(msg => {
        writeQueue.push({
            ...msg.payload,
            queuedAt: Date.now(),
            attempts: 0
        });
    });
} else {
    writeQueue.push({
        ...msg.payload,
        queuedAt: Date.now(),
        attempts: 0
    });
}

// Check if we should process queue
const queueConfig = flow.get('opcua_queue_config') || {
    batchSize: 50,
    flushInterval: 1000,
    maxRetries: 3
};

if (writeQueue.length >= queueConfig.batchSize) {
    // Process batch
    const batch = writeQueue.splice(0, queueConfig.batchSize);
    flow.set('opcua_write_queue', writeQueue);

    // Create write message
    msg.payload = {
        action: 'writeBatch',
        items: batch.map(item => ({
            nodeId: item.nodeId,
            value: item.value,
            dataType: item.dataType
        }))
    };

    return msg;
}

// Save queue
flow.set('opcua_write_queue', writeQueue);

// Set up flush timer if not already running
if (!flow.get('opcua_queue_timer')) {
    const timerId = setInterval(() => {
        const queue = flow.get('opcua_write_queue') || [];
        if (queue.length > 0) {
            const batch = queue.splice(0, queueConfig.batchSize);
            flow.set('opcua_write_queue', queue);

            node.send({
                payload: {
                    action: 'writeBatch',
                    items: batch.map(item => ({
                        nodeId: item.nodeId,
                        value: item.value,
                        dataType: item.dataType
                    }))
                }
            });
        }
    }, queueConfig.flushInterval);

    flow.set('opcua_queue_timer', timerId);
}

return null;

finalize:
// Clean up timer
const timerId = flow.get('opcua_queue_timer');
if (timerId) {
    clearInterval(timerId);
    flow.set('opcua_queue_timer', null);
}

