// Format write request for OPC UA client node
const batch = msg.payload;

// Convert to OPC UA client format
msg.payload = batch.items.map(item => {
    // Map data types to OPC UA types
    let opcType;
    switch(item.dataType) {
        case 'Float':
        case 'Double':
            opcType = 'Double';
            break;
        case 'Integer':
        case 'Int32':
            opcType = 'Int32';
            break;
        case 'Boolean':
            opcType = 'Boolean';
            break;
        case 'String':
            opcType = 'String';
            break;
        case 'DateTime':
            opcType = 'DateTime';
            break;
        default:
            opcType = 'Variant';
    }

    return {
        nodeId: item.nodeId,
        value: {
            value: item.value,
            dataType: opcType
        }
    };
});

msg.action = 'write';

// Update statistics
let stats = flow.get('opcua_connection_state')?.statistics || {};
stats.writes = (stats.writes || 0) + batch.items.length;

return msg;

// Handle responses from OPC UA operations
const response = msg.payload;

if (msg.error) {
    // Handle error
    node.error(`OPC UA Error: ${msg.error}`);

    // Update error statistics
    let state = flow.get('opcua_connection_state') || {};
    state.statistics.errors = (state.statistics.errors || 0) + 1;
    state.lastError = msg.error;
    flow.set('opcua_connection_state', state);

    // Check if connection error
    if (msg.error.includes('connection') || msg.error.includes('timeout')) {
        msg.payload = {
            action: 'error',
            error: msg.error
        };
        return [msg, null];
    }

    return [null, msg];
}

// Successful response
if (response.statusCode && response.statusCode.name === 'Good') {
    // Update success statistics
    let state = flow.get('opcua_connection_state') || {};
    if (msg.action === 'write') {
        state.statistics.writes = (state.statistics.writes || 0) + 1;
    } else if (msg.action === 'read') {
        state.statistics.reads = (state.statistics.reads || 0) + 1;
    }
    flow.set('opcua_connection_state', state);
}

msg.payload = {
    success: true,
    action: msg.action,
    response: response,
    timestamp: new Date()
};

return [msg, null];

// Browse OPC UA tag structure to verify Ignition tags
const action = msg.payload.action || 'browse';
const path = msg.payload.path || '[default]';

if (action === 'browse') {
    // Format browse request
    msg.payload = {
        nodeId: `ns=2;s=${path}`,
        browseDirection: 'Forward',
        nodeClassMask: 0, // All node classes
        resultMask: 63 // All attributes
    };
    msg.action = 'browse';

    // Store current browse path
    flow.set('current_browse_path', path);

    return msg;

} else if (action === 'read') {
    // Read specific tag value
    msg.payload = {
        nodeId: `ns=2;s=${path}`
    };
    msg.action = 'read';

    return msg;
}

return null;

// Process browse results and format for display
const results = msg.payload;
const currentPath = flow.get('current_browse_path') || '[default]';

if (!results || !results.references) {
    return null;
}

// Parse browse results
const items = results.references.map(ref => {
    const browseName = ref.browseName?.name || 'Unknown';
    const nodeClass = ref.nodeClass || 'Unknown';
    const nodeId = ref.nodeId?.value || '';

    // Extract path from nodeId
    let path = nodeId;
    if (nodeId.startsWith('ns=2;s=')) {
        path = nodeId.substring(7); // Remove 'ns=2;s=' prefix
    }

    return {
        name: browseName,
        path: path,
        nodeClass: nodeClass,
        hasChildren: nodeClass === 'Object' || nodeClass === 'Folder',
        isTag: nodeClass === 'Variable',
        displayPath: path.replace('[default]', '')
    };
});

// Sort folders first, then tags
items.sort((a, b) => {
    if (a.hasChildren && !b.hasChildren) return -1;
    if (!a.hasChildren && b.hasChildren) return 1;
    return a.name.localeCompare(b.name);
});

// Store browse results
flow.set('browse_results', {
    path: currentPath,
    items: items,
    timestamp: new Date()
});

msg.payload = {
    currentPath: currentPath,
    items: items
};

return msg;

// Convert normalized protocol data to OPC UA writes
const data = msg.payload;

// Check if this is equipment data that should be written to OPC UA
if (!data.equipment || !data.equipment.id) {
    return null; // No equipment mapping, skip
}

// Format for OPC UA mapper
msg.payload = {
