// Sync equipment registrations with Ignition OPC UA
const action = msg.payload.action || 'sync';

if (action === 'sync_all') {
    // Get all equipment registrations
    const registrations = flow.get('equipment_registrations') || {};
    const messages = [];

    Object.values(registrations).forEach(equipment => {
