};

// Update statistics based on message type
if (stats.type === 'data_written') {
    flowStats.totalMessages++;

    // Track by protocol
    const protocol = stats.source?.protocol || 'unknown';
    flowStats.messagesPerProtocol[protocol] = 
        (flowStats.messagesPerProtocol[protocol] || 0) + 1;

    // Track by equipment
    const equipment = stats.equipmentId || 'unknown';
    flowStats.messagesPerEquipment[equipment] = 
        (flowStats.messagesPerEquipment[equipment] || 0) + 1;

    // Track hourly
    flowStats.lastHour.push({
