    });

} else if (stats.type === 'error') {
    flowStats.errors++;
}

// Clean old hourly data
const oneHourAgo = Date.now() - (60 * 60 * 1000);
flowStats.lastHour = flowStats.lastHour.filter(item => 
    item.timestamp.getTime() > oneHourAgo
);

// Calculate rates
const messagesPerMinute = flowStats.lastHour.filter(item => 
    item.timestamp.getTime() > (Date.now() - 60000)
).length;

// Save updated stats
flow.set('data_flow_stats', flowStats);

