// Validate OPC UA tag structure matches expected ISA-95 format
const action = msg.payload.action || 'validate';

if (action === 'validate_structure') {
    const expectedStructure = {
        '[default]Enterprise': {
            'SteelBonnet': {
                'MainBrewery': {
                    'BrewHouse': ['MillRoom', 'MashTun', 'LauterTun', 'BrewKettle', 'Whirlpool'],
                    'CellarHouse': ['Fermentation', 'BriteTanks', 'CIPStation', 'YeastManagement'],
                    'Packaging': ['Bottling', 'Canning', 'Kegging'],
                    'Utilities': ['Boilers', 'Chillers', 'CompressedAir', 'WaterTreatment']
                }
