        }
    };

    // Generate validation requests
    const validationRequests = [];

    function generatePaths(obj, currentPath = '') {
        Object.entries(obj).forEach(([key, value]) => {
            const path = currentPath ? `${currentPath}/${key}` : key;

            validationRequests.push({
                payload: {
                    action: 'browse',
