} else if (action === 'validation_result') {
    // Process validation result
    const results = flow.get('validation_results') || [];
    const requests = flow.get('validation_requests') || [];
    const index = flow.get('validation_index') || 0;

    // Check result
    const expectedChildren = requests[index]?.expectedChildren || [];
    const actualChildren = msg.payload.items?.map(item => item.name) || [];

    const missing = expectedChildren.filter(child => !actualChildren.includes(child));
    const extra = actualChildren.filter(child => !expectedChildren.includes(child));

    results.push({
        path: msg.payload.currentPath,
        valid: missing.length === 0,
        missing: missing,
        extra: extra,
        timestamp: new Date()
    });

    flow.set('validation_results', results);

    // Continue validation
    const nextIndex = index + 1;
    if (nextIndex < requests.length) {
        flow.set('validation_index', nextIndex);
        return requests[nextIndex];
    } else {
        // Validation complete
        msg.payload = {
            action: 'validation_complete',
            results: results,
            totalPaths: requests.length,
            validPaths: results.filter(r => r.valid).length,
            issues: results.filter(r => !r.valid)
        };
