    // Split large groups into smaller batches
    for (let i = 0; i < writes.length; i += maxBatchSize) {
        optimizedBatches.push({
            path: path,
            writes: writes.slice(i, i + maxBatchSize),
            priority: path.includes('Alarm') ? 'high' : 'normal'
        });
    }
});

// Sort by priority
optimizedBatches.sort((a, b) => {
    if (a.priority === 'high' && b.priority !== 'high') return -1;
    if (a.priority !== 'high' && b.priority === 'high') return 1;
    return 0;
});

// Send batches with delay to prevent overwhelming server
optimizedBatches.forEach((batch, index) => {
    setTimeout(() => {
        node.send({
            payload: batch.writes,
            topic: 'opcua/batch_write',
            batchInfo: {
                path: batch.path,
                size: batch.writes.length,
                priority: batch.priority,
                batchNumber: index + 1,
                totalBatches: optimizedBatches.length
            }
        });
    }, index * 100); // 100ms between batches
});

return null;

