        deviceName: event.deviceName || 'Unknown Device',
        deviceID: event.deviceID,
        channel: event.channel || 0,
        hubPort: event.hubPort || -1,
        isHubPort: event.isHubPort || false,
        attached: true,
        discoveredAt: new Date().toISOString(),
        capabilities: []
    };

    // Determine device capabilities based on class
    switch (event.deviceClass) {
        case 'PhidgetTemperatureSensor':
            device.capabilities = ['temperature'];
            device.type = 'temperature';
            break;
        case 'PhidgetDigitalInput':
            device.capabilities = ['digital_input'];
            device.type = 'digital_input';
            break;
        case 'PhidgetDigitalOutput':
            device.capabilities = ['digital_output', 'relay'];
            device.type = 'relay';
            break;
        case 'PhidgetVoltageInput':
            device.capabilities = ['analog_input', 'voltage'];
            device.type = 'analog_input';
            break;
        case 'PhidgetHumiditySensor':
            device.capabilities = ['humidity', 'temperature'];
            device.type = 'humidity';
            break;
        default:
            device.capabilities = ['unknown'];
            device.type = 'unknown';
    }

    // Store device in catalog
    let catalog = flow.get('phidget_catalog') || {};
    const deviceKey = `${device.serialNumber}_${device.channel}`;
    catalog[deviceKey] = device;
    flow.set('phidget_catalog', catalog);

