    let catalog = flow.get('phidget_catalog') || {};
    const deviceKey = `${event.serialNumber}_${event.channel || 0}`;

    if (catalog[deviceKey]) {
        catalog[deviceKey].attached = false;
        catalog[deviceKey].detachedAt = new Date().toISOString();
        flow.set('phidget_catalog', catalog);
