// Scan Node-RED flows for valve control logic
const action = msg.payload.action || 'scan';

if (action === 'scan') {
    // Initialize discovery results
    let discoveryResults = {
        valves: {},
        controlLogic: {},
        mqttTopics: {
            command: [],
            status: [],
            feedback: []
        },
        dependencies: {},
        timestamp: new Date()
    };

    // Get all MQTT nodes that might be valve-related
    const mqttTopics = flow.get('mqtt_catalog') || {};

    // Identify valve-related topics
    Object.keys(mqttTopics).forEach(topic => {
        if (topic.includes('valve') || topic.includes('vlv') || topic.includes('V0')) {
            // Categorize topics
            if (topic.includes('command') || topic.includes('cmd') || topic.includes('set')) {
                discoveryResults.mqttTopics.command.push(topic);
            } else if (topic.includes('status') || topic.includes('state')) {
                discoveryResults.mqttTopics.status.push(topic);
            } else if (topic.includes('feedback') || topic.includes('fb')) {
                discoveryResults.mqttTopics.feedback.push(topic);
            }

            // Extract valve identifier
            const valveMatch = topic.match(/(valve|vlv|V)(\d+)/i);
            if (valveMatch) {
                const valveId = `VALVE_${valveMatch[2]}`;

                if (!discoveryResults.valves[valveId]) {
                    discoveryResults.valves[valveId] = {
                        id: valveId,
                        topics: [],
                        controlType: 'unknown',
                        lastSeen: mqttTopics[topic].lastSeen,
                        messageCount: mqttTopics[topic].messageCount
                    };
                }

                discoveryResults.valves[valveId].topics.push(topic);
            }
        }
    });

    // Store discovery results
    flow.set('valve_discovery_results', discoveryResults);

    msg.payload = {
        action: 'discovery_complete',
        results: discoveryResults,
        valveCount: Object.keys(discoveryResults.valves).length,
        topicCount: discoveryResults.mqttTopics.command.length + 
                    discoveryResults.mqttTopics.status.length + 
                    discoveryResults.mqttTopics.feedback.length
    };

    return msg;
}

return null;

// Analyze discovered valve control patterns
const results = msg.payload.results;

// Analyze each valve to determine control pattern
Object.entries(results.valves).forEach(([valveId, valve]) => {
    const patterns = {
        hasCommand: false,
        hasStatus: false,
        hasFeedback: false,
        isFailsafe: false,
        hasInterlocks: false,
        controlMode: 'unknown'
    };

    // Check what topics exist for this valve
    valve.topics.forEach(topic => {
        if (topic.includes('command') || topic.includes('cmd')) {
            patterns.hasCommand = true;
        }
        if (topic.includes('status') || topic.includes('state')) {
            patterns.hasStatus = true;
        }
        if (topic.includes('feedback') || topic.includes('fb')) {
            patterns.hasFeedback = true;
        }
        if (topic.includes('failsafe') || topic.includes('emergency')) {
            patterns.isFailsafe = true;
        }
        if (topic.includes('interlock') || topic.includes('permit')) {
            patterns.hasInterlocks = true;
        }
    });

    // Determine control mode
    if (patterns.hasCommand && patterns.hasFeedback) {
        patterns.controlMode = 'closed_loop';
    } else if (patterns.hasCommand && patterns.hasStatus) {
        patterns.controlMode = 'open_loop_monitored';
    } else if (patterns.hasCommand) {
        patterns.controlMode = 'open_loop';
    } else if (patterns.hasStatus) {
        patterns.controlMode = 'monitor_only';
    }

    // Update valve info
    valve.controlPattern = patterns;
    valve.complexity = calculateComplexity(patterns);
});

// Group valves by complexity for migration planning
const migrationGroups = {
    simple: [],      // Basic on/off, no interlocks
    moderate: [],    // With feedback or simple interlocks
    complex: [],     // Failsafe, multiple interlocks
    monitor: []      // Monitor only, no control
};

Object.entries(results.valves).forEach(([valveId, valve]) => {
    switch(valve.complexity) {
        case 'simple':
            migrationGroups.simple.push(valveId);
            break;
        case 'moderate':
            migrationGroups.moderate.push(valveId);
            break;
        case 'complex':
            migrationGroups.complex.push(valveId);
            break;
        default:
            migrationGroups.monitor.push(valveId);
    }
});

results.migrationGroups = migrationGroups;
results.analysisComplete = true;

// Save analysis
flow.set('valve_analysis_results', results);

msg.payload = results;
return msg;

function calculateComplexity(patterns) {
    let score = 0;

    if (patterns.hasCommand) score += 1;
    if (patterns.hasFeedback) score += 1;
    if (patterns.hasInterlocks) score += 2;
    if (patterns.isFailsafe) score += 2;

    if (score >= 4) return 'complex';
    if (score >= 2) return 'moderate';
    if (score >= 1) return 'simple';
    return 'monitor';
}

