// Analyze valve interlock dependencies
const valves = flow.get('valve_analysis_results')?.valves || {};

// Build dependency graph
const dependencies = {};
const interlockMap = {};

// Scan for interlock patterns in MQTT topics
const mqttCatalog = flow.get('mqtt_catalog') || {};

Object.keys(mqttCatalog).forEach(topic => {
    // Look for interlock topics
    if (topic.includes('interlock') || topic.includes('permit')) {
        // Extract valve references
        const valveRefs = topic.match(/(valve|vlv|V)(\d+)/gi) || [];

        if (valveRefs.length >= 2) {
            // This topic links multiple valves
            const valve1 = `VALVE_${valveRefs[0].match(/\d+/)[0]}`;
            const valve2 = `VALVE_${valveRefs[1].match(/\d+/)[0]}`;

            if (!dependencies[valve1]) dependencies[valve1] = [];
            if (!dependencies[valve2]) dependencies[valve2] = [];

            dependencies[valve1].push({
                type: 'interlock',
                target: valve2,
                topic: topic
            });

            dependencies[valve2].push({
                type: 'interlock',
                target: valve1,
                topic: topic
            });
        }
    }

    // Look for cascade or sequence patterns
    if (topic.includes('sequence') || topic.includes('cascade')) {
        const match = topic.match(/sequence_(\d+)/i);
        if (match) {
            interlockMap[`SEQ_${match[1]}`] = topic;
        }
    }
});

// Analyze dependency complexity
const complexityAnalysis = {
    isolated: [],      // No dependencies
    simple: [],        // 1-2 dependencies
    moderate: [],      // 3-5 dependencies
    complex: [],       // 6+ dependencies
    circular: []       // Circular dependencies detected
};

// Check for circular dependencies
function hasCircularDependency(valve, visited = new Set()) {
    if (visited.has(valve)) return true;
    visited.add(valve);

    const deps = dependencies[valve] || [];
    for (const dep of deps) {
        if (hasCircularDependency(dep.target, new Set(visited))) {
            return true;
        }
    }

    return false;
}

// Categorize valves by dependency complexity
Object.keys(valves).forEach(valveId => {
    const deps = dependencies[valveId] || [];
    const depCount = deps.length;

    if (hasCircularDependency(valveId)) {
        complexityAnalysis.circular.push(valveId);
    } else if (depCount === 0) {
        complexityAnalysis.isolated.push(valveId);
    } else if (depCount <= 2) {
        complexityAnalysis.simple.push(valveId);
    } else if (depCount <= 5) {
        complexityAnalysis.moderate.push(valveId);
    } else {
        complexityAnalysis.complex.push(valveId);
    }
});

// Store analysis
flow.set('valve_dependencies', {
    dependencies: dependencies,
    interlockMap: interlockMap,
    complexityAnalysis: complexityAnalysis,
    timestamp: new Date()
});

msg.payload = {
    dependencies: dependencies,
    analysis: complexityAnalysis,
    interlockCount: Object.keys(interlockMap).length
};

return msg;

