if (inactive.length > 0) {
    msg.payload = `Critical flows not active: ${inactive.join(', ')}`;
    msg.highlight = 'red';
    return msg;
}

return null;

// Process equipment registration
const action = msg.payload.action;
const equipment = msg.payload.equipment;

// Get or initialize registry
let registry = global.get('equipment_registry') || [];

if (action === 'register') {
    // Check if already exists
    const exists = registry.find(e => e.id === equipment.id);

    if (!exists) {
        registry.push(equipment);
        global.set('equipment_registry', registry);

        msg.payload = {
            success: true,
            message: `Registered ${equipment.name}`,
            equipment: equipment,
            totalRegistered: registry.length
        };
    } else {
        msg.payload = {
            success: false,
            message: `${equipment.name} already registered`,
            equipment: exists
        };
    }
}

// Update flow stats
const flowStats = global.get('flowStats') || {};
if (!flowStats['equipment_registration_flow']) {
    flowStats['equipment_registration_flow'] = { count: 0, active: true, lastMessage: new Date() };
}
flowStats['equipment_registration_flow'].count++;
flowStats['equipment_registration_flow'].lastMessage = new Date();
global.set('flowStats', flowStats);

return msg;

// Process incoming OPC UA data
const nodeId = msg.payload.nodeId;
const value = msg.payload.value.value;
const quality = msg.payload.value.statusCode?.name || 'Good';

// Normalize for protocol core
msg.payload = {
    protocol: 'opcua',
    nodeId: nodeId,
    value: value,
    quality: quality,
    timestamp: new Date(),
    sourceTimestamp: msg.payload.sourceTimestamp
};

// Update flow stats
const flowStats = global.get('flowStats') || {};
if (!flowStats['opcua_client_bridge_flow']) {
    flowStats['opcua_client_bridge_flow'] = { count: 0, active: true, lastMessage: new Date() };
}
flowStats['opcua_client_bridge_flow'].count++;
flowStats['opcua_client_bridge_flow'].lastMessage = new Date();
global.set('flowStats', flowStats);

return msg;

// Monitor OPC UA tag values
let tagValues = flow.get('opc_tag_values') || {};

tagValues[msg.payload.nodeId] = {
    value: msg.payload.value,
    quality: msg.payload.quality,
    timestamp: msg.payload.timestamp
};

flow.set('opc_tag_values', tagValues);

// Send to protocol core
msg.protocol = 'opcua';
return msg;

// Process OPC UA sync request
const action = msg.payload.action;

if (action === 'sync') {
    // Update sync status
    flow.set('last_sync', {
        time: new Date(),
        tagCount: msg.payload.tagCount,
        status: msg.payload.status
    });

    // Update flow stats
    const flowStats = global.get('flowStats') || {};
    if (!flowStats['opcua_data_sync_flow']) {
        flowStats['opcua_data_sync_flow'] = { count: 0, active: true, lastMessage: new Date() };
    }
    flowStats['opcua_data_sync_flow'].count++;
    flowStats['opcua_data_sync_flow'].lastMessage = new Date();
    global.set('flowStats', flowStats);

    msg.payload = {
        syncComplete: true,
        tagCount: msg.payload.tagCount,
        timestamp: new Date()
    };
}

return msg;

// Rate limit error logging to prevent memory issues
const errorCount = context.get('errorCount') || {};
const now = Date.now();
const key = msg.error.message.substring(0, 50);

if (!errorCount[key]) {
    errorCount[key] = { count: 0, lastLogged: 0 };
}

errorCount[key].count++;

// Only log once per minute per error type
if (now - errorCount[key].lastLogged > 60000) {
    errorCount[key].lastLogged = now;
    node.error(`Error (${errorCount[key].count} occurrences): ${msg.error.message}`, msg);
    errorCount[key].count = 0;
}

context.set('errorCount', errorCount);
return null; // Don't pass error further
