// Parse incoming commands from n8n
const data = msg.payload;
const topic = msg.topic;

// Extract command info from topic: iiot/commands/equipmentId/action
const topicParts = topic.split('/');
if (topicParts.length >= 4) {
    const equipmentId = topicParts[2];
    const action = topicParts[3];

    // Validate command structure
    if (data && data.command && data.value !== undefined) {
        // Map equipment ID to OPC-UA node paths
        const equipmentMapping = {
            'Pumps_Pump_1': 'Steel_Bonnet/Equipment/Pumps/Pump_1',
            'Pumps_Pump_2': 'Steel_Bonnet/Equipment/Pumps/Pump_2',
            'Boilers_Boiler_1': 'Steel_Bonnet/Equipment/Boilers/Boiler_1',
            'GlycolChillers_Chiller_1': 'Steel_Bonnet/Equipment/GlycolChillers/Chiller_1',
            'AirCompressors_Compressor_1': 'Steel_Bonnet/Equipment/AirCompressors/Compressor_1',
            'WalkInChillers_Chiller_1': 'Steel_Bonnet/Equipment/WalkInChillers/Chiller_1'
        };

        const equipmentPath = equipmentMapping[equipmentId];
        if (!equipmentPath) {
            node.error(`Unknown equipment ID: ${equipmentId}`);
            return null;
        }

        // Create OPC-UA write command
        const nodeId = `ns=2;s=${equipmentPath}/${data.parameter || action}`;

        msg.payload = {
            nodeId: nodeId,
            value: data.value,
            datatype: data.datatype || 'Double',
            command: data.command,
            equipmentId: equipmentId,
            action: action,
            timestamp: new Date().toISOString(),
            source: 'n8n_command'
        };

        return msg;
    }
}

node.error('Invalid command format');
return null;

// Validate commands before writing to Ignition
const cmd = msg.payload;

// Define allowed commands and ranges
const commandValidation = {
    'Pumps_Pump_1': {
        'FlowRateSetpoint': { min: 0, max: 150, datatype: 'Double' },
        'Start': { value: true, datatype: 'Boolean' },
        'Stop': { value: false, datatype: 'Boolean' },
        'Reset': { value: true, datatype: 'Boolean' }
    },
    'Pumps_Pump_2': {
        'FlowRateSetpoint': { min: 0, max: 140, datatype: 'Double' },
        'Start': { value: true, datatype: 'Boolean' },
        'Stop': { value: false, datatype: 'Boolean' },
        'Reset': { value: true, datatype: 'Boolean' }
    },
    'Boilers_Boiler_1': {
        'TemperatureSetpoint': { min: 160, max: 220, datatype: 'Double' },
        'PressureSetpoint': { min: 15, max: 35, datatype: 'Double' },
        'Start': { value: true, datatype: 'Boolean' },
        'Stop': { value: false, datatype: 'Boolean' },
        'EmergencyShutdown': { value: true, datatype: 'Boolean' }
    },
    'GlycolChillers_Chiller_1': {
        'SupplyTempSetpoint': { min: 26, max: 34, datatype: 'Double' },
        'Start': { value: true, datatype: 'Boolean' },
        'Stop': { value: false, datatype: 'Boolean' }
    },
    'AirCompressors_Compressor_1': {
        'PressureSetpoint': { min: 80, max: 130, datatype: 'Double' },
        'Start': { value: true, datatype: 'Boolean' },
        'Stop': { value: false, datatype: 'Boolean' }
    }
};

const equipmentValidation = commandValidation[cmd.equipmentId];
if (!equipmentValidation) {
    node.error(`No validation rules for equipment: ${cmd.equipmentId}`);
    return null;
}

const paramValidation = equipmentValidation[cmd.action];
if (!paramValidation) {
    node.error(`Invalid command '${cmd.action}' for equipment '${cmd.equipmentId}'`);
    return null;
}

// Validate value ranges
if (paramValidation.min !== undefined && cmd.value < paramValidation.min) {
    node.error(`Value ${cmd.value} below minimum ${paramValidation.min}`);
    return null;
}

if (paramValidation.max !== undefined && cmd.value > paramValidation.max) {
    node.error(`Value ${cmd.value} above maximum ${paramValidation.max}`);
    return null;
}

// Set correct datatype
cmd.datatype = paramValidation.datatype;

// For fixed value commands, override the value
if (paramValidation.value !== undefined) {
    cmd.value = paramValidation.value;
}

node.status({fill:'green', shape:'dot', text:`Validated: ${cmd.equipmentId} ${cmd.action}`});

return msg;

// Prepare message for OPC-UA write
const cmd = msg.payload;

// Create OPC-UA Item message
msg.payload = {
    messageType: 'Variable',
    variableName: cmd.nodeId,
    variableValue: cmd.value,
    attributeId: 13 // Value attribute
};

msg.topic = cmd.nodeId;

// Log the write operation
const logEntry = {
    timestamp: cmd.timestamp,
    equipmentId: cmd.equipmentId,
    action: cmd.action,
    nodeId: cmd.nodeId,
    value: cmd.value,
    datatype: cmd.datatype,
    source: cmd.source
};

// Store in flow context for audit trail
let writeLog = flow.get('ignition_write_log') || [];
writeLog.push(logEntry);

// Keep only last 100 entries
if (writeLog.length > 100) {
    writeLog = writeLog.slice(-100);
}

flow.set('ignition_write_log', writeLog);

node.status({fill:'blue', shape:'ring', text:`Writing: ${cmd.action} = ${cmd.value}`});

return msg;

// Handle the result of the OPC-UA write
const success = msg.statusCode && msg.statusCode.name === 'Good';

if (success) {
    node.status({fill:'green', shape:'dot', text:'Write successful'});

    // Publish success confirmation
    msg.payload = {
        status: 'success',
        message: 'Command executed successfully',
        timestamp: new Date().toISOString()
    };
    msg.topic = 'iiot/commands/status';
} else {
    node.status({fill:'red', shape:'ring', text:'Write failed'});

    // Publish error
    msg.payload = {
        status: 'error',
        message: `Write failed: ${msg.statusCode ? msg.statusCode.description : 'Unknown error'}`,
        timestamp: new Date().toISOString()
    };
    msg.topic = 'iiot/commands/status';
}

return msg;
